function setExcel()
{
	var fName   = "출고요구확정일자수정";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|출고요구확정일자수정";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


