function fn_print()
{
	if(ds_BZOE211Q.rowcount <= 0){
		alert("[알림] 조회후 발행바랍니다.");
		return;
	}

	var sysDt =ds_SYSDATE.GetColumn(0,"DT");
	var systm =ds_SYSDATE.GetColumn(0,"TM");

	ubi0.Visible = true;
	ubi0.setJrfFileDir(G_baseURL+"ui/PSIMMLIB/REPORT/");

	Ubi0.setJrfFileName("BZOE211R.jrf");

	Ubi0.SetDataset("ds_BZOE211Q", "ds_BZOE211Q");
	Ubi0.SetArgument("COMPS",cb_searchCodeName.Value);
	Ubi0.SetArgument("REQDTF",ca_plnym_from.Value);
	Ubi0.SetArgument("REQDTT",ca_plnym_to.Value);
	Ubi0.SetArgument("SYSDATE",sysDt);
	Ubi0.SetArgument("SYSTIME",systm);	
	Ubi0.Retrieve();
}

