 /*--------------------------------------------------------------
 * grid에서 expr로 구현 함수
----------------------------------------------------------------*/
function fn_reson(EFLG50,EFLG51,EFLG52,EFLG53,EFLG54)
{
	rtnmsg ="정상";
	if(EFLG50 == "E") {
		rtnmsg = "ITEM MASTER NOT FOUND  ";
	}
	if(EFLG51 == "E") {
		rtnmsg = rtnmsg + "INVENTORY MASTER NOT FOUND  ";
	}
	if(EFLG52 == "E") {
		rtnmsg = rtnmsg + "DPPSIM NOT FOUND  ";
	}
	if(EFLG53 == "E") {
		rtnmsg = rtnmsg + "PURCHASE PRICE MASTER NOT FOUND  ";
	}
	if(EFLG54 == "E") {
		rtnmsg = rtnmsg + "재고수량부족  ";
	}
	return rtnmsg;
}
