/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function On_LoadCompleted(obj)
{
	getTitle();	
	getDesign();
	//getComCode();
	MainGrid.UserData = MainGrid.Height;
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 		
	ed_CHKYY.Text = left(nowDate,4);
	ed_Action.SetFocus();

}

/********************************************************************** 
 * 내용설명 : 공통디자인 설정
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
}

/********************************************************************** 
 * 내용설명 : 사업부 선택
 * 주요변수 : 
 **********************************************************************/ 
function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value = comps;


}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getBZZE250(){
	var sKind		= "getBZZE250";				
	var sMethodName = "service::psimmlib/selectBZZE250.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BZZE250=ds_BZZE250";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";	
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@CHKYY=" + quote(trim(ed_CHKYY.Text));
	sArgument += "@MM=" + quote(substr(nowDate,4,2));

	http.Sync = true;
	var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}


/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 * 			
 **********************************************************************/ 

 function setBZZE250(){
 	if(!validationChk()){
		return;
	}
	var sKind		= "setBZZE250";						
	var sMethodName = "service::psimmlib/saveBZZE250.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_BZZE250=ds_BZZE250:U";                      // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_BZZE250_Check=ds_BZZE250_Check";							// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " CHKYY=" + quote(substr(displayDiv.ca_MDATE.Value,0,4));
	sArgument += " MM=" + quote(substr(displayDiv.ca_MDATE.Value,4,2));
	//sArgument += " JPNNO=" + quote(trim(ed_JPNNO.Text));
	//sArgument += " CONNO=" + quote(ed_CONNO.Text);
	//sArgument += " TRNDT=" + quote(ca_TRNDT.Value);
	//sArgument += " HOUSE=" + quote(ed_HOUSE.Text);

	http.Sync=true;
	//var str = dialog("Common::PGMLOAD_IMM.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
 }

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){

	if (svcId == "setBZZE250"){
		if (ds_BZZE250_Check.GetRowCount() > 0){			
			for(var i=0;i<ds_BZZE250_Check.GetRowCount();i++){		
				alert(ds_BZZE250_Check.GetColumn(i,"PSPNO")+" : "+ds_BZZE250_Check.GetColumn(i,"RTMSG"));	
			}
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}
/********************************************************************** 
 * 내용설명 : 컴포넌트 validation 체크
 **********************************************************************/ 
function validationChk(){
	var result = true;
	if(length(trim(displayDiv.ed_PSPNO.text)) < 1){
		alert(setPopMessage("1501")); //[[확인] 품번 확인바랍니다.
		setMessage("1501");
		displayDiv.ed_PSPNO.SetFocus();
		return false;
	}
	if(length(trim(displayDiv.ca_MDATE.Value)) < 1){
		alert(setPopMessage("1501")); //[[확인] 품번 확인바랍니다.
		setMessage("1501");
		displayDiv.ed_PSPNO.SetFocus();
		return false;
	}
	return true;
}


function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell <= 7) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{		
	if(bCtrl == 1 && nChar == 67) {
		grd_fn_ClipboardCopy(obj, strSeparator);
	}	
}

