function fn_Search()
{
	ed_Action.Text = "Q";
	getBZZE250();
}

function fn_Input()
{
	if(checkInsertCount("ds_BZZE250")== false) return;

	if(ds_BZZE250.rowcount < 1){
		getBZZE250();
	}

	ed_Action.Text = "A";

	ds_BZZE250.AddRow();
	ds_BZZE250.SetColumn(ds_BZZE250.row,"CHKYMD",nowDate);
	displayDiv.ed_PSPNO.Enable = true;
	displayDiv.ed_PSPNO.SetFocus();
}

function fn_Delete()
{
	if(!validationChk()) return;
	ds_BZZE250.DeleteRow(ds_BZZE250.currow);
}

function fn_Save()
{
	setBZZE250();
}


