/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "입고검사방법등록";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|입고검사방법등록";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

function ed_Action_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		switch(obj.text){
			case "A" : fn_Input(); break;
			case "Q" : fn_Search(); break;
			default : alert(setPopMessage("1016")); setMessage("1016");
		}
	}
}

function ds_BZZE250_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "PSPNO" ){
		//품번 Check
		var sKind		= "getBM008PR";		
		var sMethodName = "service::pscomlib/selectBM008PR.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_BM008PR=ds_BM008PR";
		var sArgument   = "";
			sArgument  += " LIB1=" + quote(Lib1);
			sArgument  += " ITNBR=" + quote(trim(varNewValue));
		http.Sync = true;	
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_BM008PR.GetRowCount() < 1){
			alert(setPopMessage("1509"));  // [경고] Item Master에 미등록된 품번입니다.
			setMessage("1509");
			displayDiv.ed_PSPNO.SetFocus();
			return false;
		}
		can_ITDSC = ds_BM008PR.GetColumn(0,"ITDSC");
	}
	if( strColumnID == "CHKMM" ){
		if (!(varNewValue == "1" || varNewValue == "2" || varNewValue == "3" || varNewValue == "" || varNewValue == " ")) {
			alert(setPopMessage("1509"));  // [경고] 검사방법을 확인바랍니다.
			setMessage("1509");
			return false;
		}
	}
	if( strColumnID == "IMPMK" ){
		if (!(varNewValue == "1" || varNewValue == "" || varNewValue == " ")) {
			alert(setPopMessage("1509"));  // [경고] 중요관리 확인바랍니다.
			setMessage("1509");
			return false;
		}
	}
}

function ds_BZZE250_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{		
	if (strColumnID == "PSPNO"){
		ds_BZZE250.SetColumn(nRow,"ITDSC",trim(can_ITDSC));
	}
	if( strColumnID == "CHKMM" ){
		ds_BZZE250.SetColumn(nRow,"CHK"+substr(displayDiv.ca_MDATE.Value,4,2),varNewValue);
	}
}

function ds_BZZE250_OnRowPosChanged(obj,nOldRow,nRow)
{
	//로우타입 insert만 ED박스 enable
	if(obj.GetRowType(obj.currow)=="insert"){
		displayDiv.ed_PSPNO.Enable=true;
	}else{
		displayDiv.ed_PSPNO.Enable=false;
	}
}

function ed_PSPNO_OnChanged(obj,strText)
{
	ds_BZZE250.ClearData();
}



function displayDiv_ed_REMAK_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		if(confirm("저장하시겠습니까? ") == false) return;
		fn_Save();
	}
}
