}

function rdoggub_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_BZZE311Q.ClearData();
	gridFormatChange();
}

function gridFormatChange() {
	switch (rdoggub.Value){
		case '1' : 
			MainGrid.SetColProp(0,"Width",36);
			MainGrid.SetColProp(1,"Width",68);
			MainGrid.SetColProp(2,"Width",236);
			MainGrid.SetColProp(3,"Width",120);
			MainGrid.SetColProp(4,"Width",240);
			MainGrid.SetColProp(5,"Width",100);
			MainGrid.SetColProp(6,"Width",100);
			MainGrid.SetColProp(7,"Width",100);
			MainGrid.SetColProp(8,"Width",56);
			MainGrid.SetCellProp("body", 3, "colid", "PRTNO");
			MainGrid.SetCellProp("body", 4, "colid", "ITDSC");
			break;
		case '2' :
			MainGrid.SetColProp(0,"Width",36);
			MainGrid.SetColProp(1,"Width",68);
			MainGrid.SetColProp(2,"Width",236);
			MainGrid.SetColProp(3,"Width",0);
			MainGrid.SetColProp(4,"Width",0);
			MainGrid.SetColProp(5,"Width",100);
			MainGrid.SetColProp(6,"Width",100);
			MainGrid.SetColProp(7,"Width",100);
			MainGrid.SetColProp(8,"Width",388);	
			break;
	}
}

function ca_CRTYM_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if(nChar == 13){
		fn_Search();
	}
}
