function setExcel()
{
	var fName   = "품목 마스타 조회";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|품목 마스타 조회";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

