function imgPspno_OnClick(obj,nX,nY)		
{	
	var sTemp=trim(ed_PSPNO.Text);	
	if (length(trim(ed_PSPNO.Text)) == 0) sTemp="zzzzzzzzzz";	
	//var selectnumber = "A1";	//A-PART만
	var selectnumber = "A0";	//전체
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);	
	if (length(rtn) != 0){	
		ed_PSPNO.Text = rtn[0];
		st_PSPNO.Text = rtn[1];
	}
}

function ed_PSPNO_OnCharChanged(obj,strPreText,strPostText)
{
	st_PSPNO.Text="";
}

function ed_PSPNO_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//품번Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectBM008PR.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_BM008PR=ds_BM008PR";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " ITNBR=" + quote(ed_PSPNO.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_BM008PR.GetRowCount() < 1){
		alert(setPopMessage("6032"));//[알림]ITEM MASTER A에 미등록된 품번입니다.
		setMessage("6032");	
		ed_PSPNO.SetFocus();
		return;
	}
	st_PSPNO.Text=ds_BM008PR.GetColumn(0,"ITDSC");
	//http.usewaitcursor = true;	
}
