#include "script::lib_script_common.js";
#include "script::Grid_Script.js";
#include "script::spare_common.js";

var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); //공장구분 선택
	MainGrid.UserData = MainGrid.Height; //높이 저장후 화면 변경시 재사용위함.
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");		
}


function getMSTZ043(){

	if(!validationChk()){
		return;
	}	

	var sKind		= "getMSTZ043";						
	var sMethodName = "service::psmstlib/selectMSTZ043.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSTZ043=ds_MSTZ043";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@MAJC2=" + quote(ed_MAJC2.Text); //사업부
	var str = dialog("Common::PGMLOAD_MST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //[알림] 신규입력입니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}	

}
/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 

 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setMSTZ043(){

	if(!validationChk()){
		return;
	}
	if(ds_MSTZ043.rowcount < 1){
		return;	
	}

	var sKind		= "setMSTZ043";						
	var sMethodName = "service::psmstlib/saveMSTZ043.do";     	// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_MSTZ043=ds_MSTZ043:U";               	//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSTZ043_CHECK=ds_MSTZ043_CHECK";  		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);	

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_MSTZ043_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_MSTZ043_CHECK.GetRowCount();i++){
			alert("코드 : " +ds_MSTZ043_CHECK.GetColumn(i,"MAJC3") + ", 계산서번호 : " +ds_MSTZ043_CHECK.GetColumn(i,"MJCHR") + " = " + ds_MSTZ043_CHECK.GetColumn(i,"RTMSG"));
		}
		getMSTZ043();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
		//alert("[경고] 처리 중 오류가 발생하였습니다.");

	}else{
		alert(setPopMessage("1001"));
		setMessage("1001");	
		//alert("[알림] 정상적으로 처리가 완료되었습니다.");
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var majc2 = ed_MAJC2.Text;
		ed_MAJC2.BKColor="white";

	if (majc2 == "")
	{
		ed_MAJC2.BKColor="paleturquoise";
		alert(setPopMessage("4055"));//[알림]HEAD코드를 입력하세요.
		setMessage("4055");
		result = false;
	}
	else if(majc2 != "JH" && majc2 != "JK" && majc2 != "JM")
	{
		ed_MAJC2.BKColor="paleturquoise";
		alert(setPopMessage("4056"));//[확인]HEAD코드는 JH(현대), JK(기아),JM(모비스)만 입력가능합니다.
		setMessage("4056");
		result = false;
	}	
	return result;
}

