function setExcel()
{
	var fName   = "세금계산서번호등록";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|세금계산서번호등록";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

