function fn_Search()
{
	getMSTZ043();
}

function fn_Input()
{
	if(!validationChk()){
		return;
	}	
	if(checkInsertCount("ds_MSTZ043")== false) return;
	if(ds_MSTZ043.rowcount < 1) fn_Search();  
	ds_MSTZ043.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
	ds_MSTZ043.AddRow();
	ds_MSTZ043.SetColumn(ds_MSTZ043.currow, "COMPS", ComboBoxPlant.Value);
	ds_MSTZ043.SetColumn(ds_MSTZ043.currow, "MAJC2", ed_MAJC2.Text);
	//ds_MSTZ043.SetRowType(ds_MSTZ043.currow, 'Normal');
	ds_MSTZ043.FireEvent=true;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
}
function fn_Delete()
{
	if(ds_MSTZ043.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_MSTZ043.DeleteRow(ds_MSTZ043.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
}

function fn_Save()
{
	setMSTZ043();
}

