function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
}

function ds_MSTZ043_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="MAJC3"){
		ds_MSTZ043.SetColumn(nRow,"MAJC3",toUpper(varNewValue));
	}
	if(strColumnID=="MJCHR"){
		ds_MSTZ043.SetColumn(nRow,"MJCHR",toUpper(varNewValue));
	}
}
