//그룹key미사용시 그룹별 카운터
function incSEQ(rowNm){

	var sSeq=1;
	var cSeq=0;
	if( rowNm==0){
		return sSeq;
	}	
	if(ds_MSTZ052Q.GetColumn(rowNm,"GRPCD") != ds_MSTZ052Q.GetColumn(rowNm-1,"GRPCD")){
		ds_MSTZ052Q.SetColumn(rowNm,"ROWNUM",1);
		sSeq=1;
	}
	else{
		cSeq=parseInt(ds_MSTZ052Q.GetColumn(rowNm-1,"ROWNUM"));
		ds_MSTZ052Q.SetColumn(rowNm,"ROWNUM",cSeq+1);
		sSeq=cSeq+1;
	}
	return sSeq;	
}
function ed_GRPCD2_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == 13){
		fn_Search();
	}
}

function ed_GRPCD2_OnCharChanged(obj,strPreText,strPostText)
{
	ds_MSTZ052Q.ClearData();	
}
