function fn_Search()
{
	getMSTZ057();
}

function fn_Input()
{
	if(checkInsertCount("ds_MSTZ057")== false) return;
	if(ds_MSTZ057.rowcount < 1) fn_Search();  
	ds_MSTZ057.AddRow();
	ds_MSTZ057.SetColumn(ds_MSTZ057.currow,"COMPS",ComboBoxPlant.Value);
	ds_MSTZ057.SetColumn(ds_MSTZ057.currow,"GRPCD","COL");
}

function fn_Delete()
{
	if(ds_MSTZ057.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_MSTZ057.DeleteRow(ds_MSTZ057.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
	setMessage("1011");
}

