function ds_MSTZ057_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="MSTCD"){ //중복체크
		if(varNewValue!=""){
			if(ds_MSTZ057.FindRow("MSTCD",varNewValue)!="-1"){
				alert(setPopMessage("2533"));
				setMessage("2533");
				ds_MSTZ057.DeleteRow(nRow);
				ds_MSTZ057.SelectRow(ds_MSTZ057.FindRow("MSTCD",varNewValue));
				return false;
			}
		}
	}
}

function ed_MSTCD_OnChanged(obj,strText)
{
	ds_MSTZ057.ClearData();	
}

function ds_MSTZ057_CanRowPosChange(obj,nOldRow,nRow)
{
	var result=true;
	if(ds_MSTZ057.GetRowType(ds_MSTZ057.currow)=="update" ||ds_MSTZ057.GetRowType(ds_MSTZ057.currow)=="insert" ){
		var mstcd=ds_MSTZ057.GetColumn(ds_MSTZ057.currow,"MSTCD");
		var mstsm=ds_MSTZ057.GetColumn(ds_MSTZ057.currow,"MSTSM");
		var mstrm=ds_MSTZ057.GetColumn(ds_MSTZ057.currow,"MSTRM");

		if(length(mstcd)!=0){
			if(length(mstsm)==0){
				alert(setPopMessage("4745"));
				setMessage("4745");
				//MainGrid.AutoEnter=true;
				result=false;
			}
			else if(length(mstrm)==0){
				alert(setPopMessage("4744"));
				setMessage("4744");
				//MainGrid.AutoEnter=true;
				result=false;
			}
		}
	return result;		
	}
}
