var nowDate = today();

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	cb_grpcd.Index = 0;
	getComCode(); //공장구분 선택
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	MainGrid.UserData = MainGrid.Height;
	ed_mstcd.SetFocus();
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053"); //상세화면 메시지
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.RowHeight = G_ds_design.GetColumn(0,"RowHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");

	displayDiv.Width = G_ds_design.GetColumn(0,"Width");
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getMSTZ058(){
	//var group = ComboBoxClass.Value;

	var sKind		= "getMSTZ058";						
	var sMethodName = "service::psmstlib/selectMSTZ058.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSTZ058=ds_MSTZ058";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@GRPCD=" + quote(cb_grpcd.Value);       //코드분류
	sArgument += "@MSTCD=" + quote(ed_mstcd.Value);      //코드
	var str = dialog("Common::PGMLOAD_MST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setMSTZ058(){
	var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);
	if(!validationChk()) return;
	var sKind		= "setMSTZ058";						
	var sMethodName = "service::psmstlib/saveMSTZ058.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_MSTZ058=ds_MSTZ058:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MSTZ058_CHECK=ds_MSTZ058_CHECK";     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " GRPCD=" + quote(displayDiv.ed_grpcd.Value);       //코드분류
	sArgument += " ADUSR=" + quote(idnum);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "setMSTZ058"){
		if (ds_MSTZ058_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_MSTZ058_CHECK.GetRowCount();i++){		
				alert(ds_MSTZ058_CHECK.GetColumn(i,"GRPCD") + " , "
					+ ds_MSTZ058_CHECK.GetColumn(i,"MSTCD") + " = "
					+ ds_MSTZ058_CHECK.GetColumn(i,"RTMSG"));	
			}
			getMSTZ058();
			return;	
		}
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setMSTZ058"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){
	for(var i=0; i<ds_MSTZ058.rowcount(); i++){
		if(length(trim(ds_MSTZ058.GetColumn(i, "GRPCD")))==0){
			alert(setPopMessage("2507"));
			setMessage("2507");
			mainGrid.SetFocus();
			ds_MSTZ058.rowposition = i;
			MainGrid.SetCellPos(0);
			return false;
		}
		if(length(trim(ds_MSTZ058.GetColumn(i, "MSTCD")))==0){
			alert(setPopMessage("2507"));
			setMessage("2507");
			mainGrid.SetFocus();
			ds_MSTZ058.rowposition = i;
			MainGrid.SetCellPos(1);
			return false;
		}
	}
	return true;
}

/********************************************************************** 
 * 내용설명 : 중복 체크
 **********************************************************************/ 
function duplicateChk(){
	http.Sync 	= true;
	var sKind		= "getMSTZ058_dchk";						
	var sMethodName = "service::psmstlib/selectMSTZ058_dchk.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_MSTZ058_dchk=ds_MSTZ058_dchk";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " GRPCD=" + quote(displayDiv.ed_grpcd.Value);       //코드분류
	sArgument += " MSTCD=" + quote(displayDiv.ed_mstcd1.Value);      //코드	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if(ds_MSTZ058_dchk.rowcount() > 0){
		alert(setPopMessage("2039"));	// 2039 == 중복된data가 존재합니다..
		setMessage("2039");
		return false;
	}
	return true;	
}

/********************************************************************** 
 * 내용설명 : validation 체크 (notnull,중복)
 **********************************************************************/ 
function ds_MSTZ058_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "MSTCD" ){
		if( length(varNewValue) == 0 ){
			alert(setPopMessage("2507"));
			setMessage("2507");
			return false;
		}
		else{
			if(!duplicateChk()){         //중복check
				displayDiv.ed_mstcd1.SetFocus();
				return false;  
				}
			return true;
		}
	}
}

/********************************************************************** 
 * 내용설명 : db2 length 체크및 변경
 **********************************************************************/ 
function ds_MSTZ058_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID == "MSTSM"){
		var newVal = chkbytelen_db2(varNewValue, 8); //변경된값과 MAxLength을 chkbyte_db2함수에 보내서 바이트 계싼함.
		if (length(newVal)!=0){ 
			ds_MSTZ058.FireEvent = false; //데이터셋의 이벤트가 발생하지 않도록 셋팅
			ds_MSTZ058.SetColumn(nRow,"MSTSM",newVal);
			ds_MSTZ058.FireEvent = true;
		}
	}
	if(strColumnID == "MSTRM"){
		var newVal = chkbytelen_db2(varNewValue, 20); //변경된값과 MAxLength을 chkbyte_db2함수에 보내서 바이트 계싼함.
		if (length(newVal)!=0){ //리턴받은 값이 0이면 STOP, 아니면 새로운값은 dataset에 set함.
			ds_MSTZ058.FireEvent = false; //데이터셋의 이벤트가 발생하지 않도록 셋팅
			ds_MSTZ058.SetColumn(nRow,"MSTRM",newVal);
			ds_MSTZ058.FireEvent = true;
		}
	}
}


/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_MSTZ058_OnRowPosChanged(obj,nOldRow,nRow){
	//로우타입 insert만 ED박스 enable
	if(ds_MSTZ058.GetRowType(ds_MSTZ058.currow)=="insert"){
		displayDiv.ed_mstcd1.Enable=true;
	}else{
		displayDiv.ed_mstcd1.Enable=false;
	}
}



