 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "자유항목10과OEM품번비교";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|자유항목10과OEM품번비교";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

