/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	//getComCode();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 
	ca_YYYY.Value = nowDate;
	if (substr(nowDate,4,2) == "04"){
		cmbSelect.Index = 0;
	}else if (substr(nowDate,4,2) == "10"){
		cmbSelect.Index = 1;
	}
	if (comps = "E1"){
		comp_NAME.Text = "DNKE (덴소코리아일렉트로닉스)";
	}else{
		comp_NAME.Text = "DNKA (덴소코리아오토모티브)";
	}

	ca_YYYY.SetFocus();
}
