/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getOCEZ400C(){
	//조회
	if(length(trim(ed_name.Text)) <1){
		alert("이름을 입력바랍니다."); 
		ed_name.SetFocus();
		return;
	}
	/* 2018.04.19 삭제
	var JDELib4;
	if (Lib2 == "PSEDBLIB"){
		JDELib4 = "CLTDTAE";
	}else{
		JDELib4 = "CLTDTAS";
	}
	*/
	var sKind		= "getOCEZ400C";
	var sMethodName = "service::psmstlib/selectOCEZ400C.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OCEZ400C=ds_OCEZ400C";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	//sArgument += "@LIB4=" + quote(JDELib4);
	sArgument += "@LIB4=" + quote(Lib4);
	sArgument += "@NAME=" + quote(trim(ed_name.Text));
	var str = dialog("Common::PGMLOAD_MST.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));  //[알림] 조회 된 데이터가 없습니다.
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}




/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

