function send_Spool(c_user,c_pass,p_id,p_jnam,p_jusr,p_jnum,p_numb,P_pgmName)
{
	//as400 USERID 실행
	callUser = c_user;
	callPass = c_pass;

	param = comps + cmbSelect.Value + 
			left(p_id+space(8),8) +
			left(p_jnam+space(10),10) +
			left(p_jusr+space(10),10) +
			left(p_jnum+space(6),6) +
			left(p_numb+space(6),6);
	for(i=length(param)+1;i<=50;i++){
		param += " ";
	}
	param += callUser;
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += "000";

	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,P_pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			ReleaseCapture();
			SetWaitCursor(false);
			switch (substr(WTA_RemoteCall0.RC_GetReturnParm(1),60,3)){
			case "999" :  
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1001")); //  정상적으로 처리가 완료되었습니다.	
				setMessage("1001");
				return true;
			case "888" :  
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				alert("NPRINT not found : 사용자SPOOL이 존재하지 않습니다.");
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				return false;
			case "777" :  
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			case "000" :  
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			default :
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
			return false;
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		ReleaseCapture();
		SetWaitCursor(false);
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
		return false;
	}
}

function cvtFile_Spool(c_user,c_pass,p_file,p_lib,p_jnam,p_jusr,p_jnum,p_numb,P_pgmName)
{
	//as400 USERID 실행
	callUser = c_user;
	callPass = c_pass;

	param = comps + cmbSelect.Value + 
			left(p_lib+space(10),10) +
			left(p_file+space(10),10) +
			left(p_jnam+space(10),10) +
			left(p_jusr+space(10),10) +
			left(p_jnum+space(6),6) +
			left(p_numb+space(6),6);
	for(i=length(param)+1;i<=60;i++){
		param += " ";
	}
	param += callUser;
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += "000";

	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

