function Tab0_OnChanging(obj,nOldIndex,nNewIndex)
{
	switch (nNewindex){
	case 3 :
		Tab0.tab4.ed_spoolCMD_1.Text = "WRKSPLF SELECT("+Tab0.tab4.ed_JOBUSR_1.Text+" *ALL *ALL "+right(Tab0.tab4.text,6)+" *ALL NPRINT) PERIOD((*AVAIL " + 
										 substr(ca_YYYY.Value,0,4) +
										 iif(cmbSelect.Value == "1","0401","1001") + 
										 ") (*AVAIL *END))";
