/********************************************************************** 
 * 내용설명 : 1.F#1122(생산계획BOM누락CHECK_LIST)
 * 주요변수 : 
 **********************************************************************/ 
function Tab0_tab2_btnQuery_OnClick(obj)
{
	var JDELib5;
	if (Lib1 == "PSEDBLIB"){
		JDELib5 = "PEWRKLIB";
	}else{
		JDELib5 = "PSWRKLIB";
	}
	var sKind		= "getOCAZ400C_tab2";						
	var sMethodName = "service::psmstlib/selectOCAZ400C_tab2.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";
		sOutDataSet = "ds_OCAZ400C_tab2=ds_OCAZ400C_tab2";		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB5=" + quote(JDELib5);
		sArgument += " TOFILE=" + quote("F#1121R");
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;
	//var fName   = substr(getDate(), 0, 8)+"_F#1122(생산계획 BOM누락 CHECK LIST).csv";  // 20150403_F#1122(생산계획 BOM누락 CHECK LIST)

	//ext_Ds2CsvEx("ds_OCAZ400C_tab2",fName,1,1,",","\r\n",0,"UTF-8");
}
function Tab0_tab2_btnExcel_OnClick(obj)
{
	if (ds_OCAZ400C_tab2.rowcount < 1){
		alert("저장할 Data가 없습니다.!");
		return;
	}
	FileDialog0.Type = "DIR";
	if (!FileDialog0.Open()) {
		alert("저장 경로를 선택하세요!");
		return;
	}
	var fName   = substr(getDate(), 0, 8)+"_F#1122(생산계획 BOM누락 CHECK LIST).xls";  // 20150403_F#1122(생산계획 BOM누락 CHECK LIST)
	var getLen=length(FileDialog0.FilePath);
	if(substr(FileDialog0.FilePath,getLen-1,1)!="\\"){
