function Tab0_tab4_btnSend_OnClick(obj)
{
	if (length(trim(TAB0.tab4.ed_TO_PWD_1.Text)) < 1){
		alert("as400 password를 입력하세요");
		return;
	}
	if (length(trim(TAB0.tab4.ed_JOBNUM_1.Text)) < 1){
		alert("Job Number를 입력하세요");
		return;
	}
	if (length(trim(TAB0.tab4.ed_SPLNBR_1.Text)) < 1){
		alert("Spool File Number를 입력하세요");
		return;
	}
	pgmName = "OCAZ400ARC";
	var c_user = trim(TAB0.tab4.ed_TO_ID_1.Text);
	var c_pass = trim(TAB0.tab4.ed_TO_PWD_1.Text);
	var p_id = trim(tab0.tab4.ed_TO_ID_1.Text);
	var p_jnam = trim(tab0.tab4.ed_JOBNAM_1.Text);
	var p_jusr = trim(tab0.tab4.ed_JOBUSR_1.Text);
	var p_jnum = trim(tab0.tab4.ed_JOBNUM_1.Text);
	var p_numb = trim(tab0.tab4.ed_SPLNBR_1.Text);
	if (send_Spool(c_user,c_pass,p_id,p_jnam,p_jusr,p_jnum,p_numb,pgmName)){
		Tab0.tab4.btnCreate.Enable = true;
	}
}
