function Tab0_tab8_btnQuery_OnClick(obj)
{
	var sKind		= "getOCAZ400C_tab8cnt";						
	var sMethodName = "service::psmstlib/selectOCAZ400C_tab8cnt.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";
		sOutDataSet = "ds_OCAZ400C_tab8cnt=ds_OCAZ400C_tab8cnt";		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " TOFILE=" + quote("F#2272UPPF");
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	var cnt = toNumber(ds_OCAZ400C_tab8cnt.GetColumn(0,"CNT"));
	var cntStep = parseInt(toNumber(cnt)/10000)+1;  //10,000건 단위로 조회
	var step;
	for(step=1 ;step<=cntStep;step++){
		var start_rownumber=1;
		var end_rownumber=0;
		start_rownumber = step*10000-9999;
		end_rownumber=step*10000;
		var sKind		= "getOCAZ400C_tab8_"+step;	
		var sMethodName = "service::psmstlib/selectOCAZ400C_tab8.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_OCAZ400C_tab8_"+step+"=ds_OCAZ400C_tab8_"+step;    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " COMPS=" + quote(comps);
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " STEP=" + quote(step);
		sArgument += " F_ROWNUM=" + quote(start_rownumber);
		sArgument += " T_ROWNUM=" + quote(end_rownumber);
		sArgument += " TOFILE=" + quote("F#2272UPPF");
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;

	}
	if (ds_OCAZ400C_tab8_1.rowcount > 0) ds_OCAZ400C_tab8.AppendDataset("ds_OCAZ400C_tab8_1");
	if (ds_OCAZ400C_tab8_2.rowcount > 0) ds_OCAZ400C_tab8.AppendDataset("ds_OCAZ400C_tab8_2");
	if (ds_OCAZ400C_tab8_3.rowcount > 0) ds_OCAZ400C_tab8.AppendDataset("ds_OCAZ400C_tab8_3");
	if (ds_OCAZ400C_tab8_4.rowcount > 0) ds_OCAZ400C_tab8.AppendDataset("ds_OCAZ400C_tab8_4");
	if (ds_OCAZ400C_tab8_5.rowcount > 0) ds_OCAZ400C_tab8.AppendDataset("ds_OCAZ400C_tab8_5");
	if (ds_OCAZ400C_tab8_6.rowcount > 0) ds_OCAZ400C_tab8.AppendDataset("ds_OCAZ400C_tab8_6");
	if (ds_OCAZ400C_tab8_7.rowcount > 0) ds_OCAZ400C_tab8.AppendDataset("ds_OCAZ400C_tab8_7");
	if (ds_OCAZ400C_tab8_8.rowcount > 0) ds_OCAZ400C_tab8.AppendDataset("ds_OCAZ400C_tab8_8");
	if (ds_OCAZ400C_tab8_9.rowcount > 0) ds_OCAZ400C_tab8.AppendDataset("ds_OCAZ400C_tab8_9");
	if (ds_OCAZ400C_tab8_10.rowcount > 0) ds_OCAZ400C_tab8.AppendDataset("ds_OCAZ400C_tab8_10");
}
