function tab12_btnQuery2(){
	var sKind		= "getOCAZ400C_tab12cnt";						
	var sMethodName = "service::psmstlib/selectOCAZ400C_tab12cnt.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";
		sOutDataSet = "ds_OCAZ400C_tab12cnt=ds_OCAZ400C_tab12cnt";		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " TOFILE=" + quote("F#D30PR");
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	var cnt = toNumber(ds_OCAZ400C_tab12cnt.GetColumn(0,"CNT"));
	var cntStep = parseInt(toNumber(cnt)/10000)+1;  //10,000건 단위로 조회

	//Dataset 생성
	for(step=1 ;step<=cntStep;step++){
		var dsObjParam = find("ds_OCAZ400C_tab12_2_"+step);
		if ( dsObjParam == null ) {
			Create("Dataset", "ds_OCAZ400C_tab12_2_"+step);
		}
	}

	var step;
	for(step=1 ;step<=cntStep;step++){
		var start_rownumber=1;
		var end_rownumber=0;
		start_rownumber = step*10000-9999;
		end_rownumber=step*10000;
		var sKind		= "getOCAZ400C_tab12_2_"+step;	
		var sMethodName = "service::psmstlib/selectOCAZ400C_tab12_2.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_OCAZ400C_tab12_2_"+step+"=ds_OCAZ400C_tab12_2_"+step;    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " COMPS=" + quote(comps);
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " STEP=" + quote(step);
		sArgument += " F_ROWNUM=" + quote(start_rownumber);
		sArgument += " T_ROWNUM=" + quote(end_rownumber);
		sArgument += " D20PR=" + quote("F#D20PR");
		sArgument += " D30PR=" + quote("F#D30PR");
		sArgument += " D2030PF=" + quote("F#D2030PF");
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
	}
	//Dataset 추가
	for(step=1 ;step<=cntStep;step++){
		if (object("ds_OCAZ400C_tab12_2_"+step).rowcount > 0) ds_OCAZ400C_tab12_2.AppendDataset("ds_OCAZ400C_tab12_2_"+step);
	}
}
function tab12_btnQuery3(){
	var sKind		= "getOCAZ400C_tab12cnt";						
	var sMethodName = "service::psmstlib/selectOCAZ400C_tab12cnt.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";
		sOutDataSet = "ds_OCAZ400C_tab12cnt=ds_OCAZ400C_tab12cnt";		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib2);  // 주의 (F#D2030PF는 PSCDBLIB에 존재함)
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " TOFILE=" + quote("F#D2030PF");
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	var cnt = toNumber(ds_OCAZ400C_tab12cnt.GetColumn(0,"CNT"));
	var cntStep = parseInt(toNumber(cnt)/10000)+1;  //10,000건 단위로 조회

	//Dataset 생성
	for(step=1 ;step<=cntStep;step++){
		var dsObjParam = find("ds_OCAZ400C_tab12_3_"+step);
		if ( dsObjParam == null ) {
			Create("Dataset", "ds_OCAZ400C_tab12_3_"+step);
		}
	}

	var step;
	for(step=1 ;step<=cntStep;step++){
		var start_rownumber=1;
		var end_rownumber=0;
		start_rownumber = step*10000-9999;
		end_rownumber=step*10000;
		var sKind		= "getOCAZ400C_tab12_3_"+step;	
		var sMethodName = "service::psmstlib/selectOCAZ400C_tab12_3.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_OCAZ400C_tab12_3_"+step+"=ds_OCAZ400C_tab12_3_"+step;    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " COMPS=" + quote(comps);
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " STEP=" + quote(step);
		sArgument += " F_ROWNUM=" + quote(start_rownumber);
		sArgument += " T_ROWNUM=" + quote(end_rownumber);
		sArgument += " D20PR=" + quote("F#D20PR");
		sArgument += " D30PR=" + quote("F#D30PR");
		sArgument += " D2030PF=" + quote("F#D2030PF");
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
	}
	//Dataset 추가
	for(step=1 ;step<=cntStep;step++){
		if (object("ds_OCAZ400C_tab12_3_"+step).rowcount > 0) ds_OCAZ400C_tab12_3.AppendDataset("ds_OCAZ400C_tab12_3_"+step);
	}
}

function tab12_btnExcel1(){
	if (ds_OCAZ400C_tab12_1.rowcount < 1){
		alert("F#4203(신구만있는품번_NEW) 저장할 Data가 없습니다.!");
		return;
	}
	alert("VDI환경에서는 저장 경로를 c:\~ 선택하세요!");
	FileDialog0.Type = "DIR";
	if (!FileDialog0.Open()) {
		alert("저장 경로를 선택하세요!");
		return;
	}
	var fName1   = substr(getDate(), 0, 8)+"_F#4203(신구만있는품번_NEW).xlsx"; 
	var getLen=length(FileDialog0.FilePath);
	if(substr(FileDialog0.FilePath,getLen-1,1)!="\\"){
		fName1= "\\"+fName1;
	}
	var ExportObject1;			
	ExportObject1 = CreateExportObject();
	ExportObject1.ExportType		= "Excel";
	ExportObject1.ExportFileName	= FileDialog0.FilePath + fName1;  //"c:\\test1111.xlsx";
	ExportObject1.ActiveSheetName   = "F#4203(신구만있는품번_NEW)";   //"test";
	ExportObject1.MakeEmptyFileWhenNotExist = true;	
	ExportObject1.AddExportGrid("F#4203(신구만있는품번_NEW)" + "!" + "A1", Tab0.tab12.MainGrid1, true, false);
	ExportObject1.Export(true, false); // 첫번째 progress, 두번째 excel
	ExportObject1.Save(); // <--------- 이 부분 추가해주세요 .
	ExportObject1.CloseWorkbook(); // <--------- 이 부분 추가해주세요 .
	ExportObject1.Close();
	ExportObject1 = null;

	alert(FileDialog0.FilePath + fName1+"으로 저장되었습니다.");
}
