		sOutDataSet = "ds_OCAZ400C_tab12cnt=ds_OCAZ400C_tab12cnt";		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote("PSWRKLIB");  // 주의 (F#4501R PSWRKLIB 존재함)
		sArgument += " TOFILE=" + quote("F#4501R");
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	var cnt = toNumber(ds_OCAZ400C_tab12cnt.GetColumn(0,"CNT"));
	var cntStep = parseInt(toNumber(cnt)/10000)+1;  //10,000건 단위로 조회

	//Dataset 생성
	for(step=1 ;step<=cntStep;step++){
		var dsObjParam = find("ds_OCAZ400C_tab13_"+step);
		if ( dsObjParam == null ) {
			Create("Dataset", "ds_OCAZ400C_tab13_"+step);
		}
	}

	var step;
	for(step=1 ;step<=cntStep;step++){
		var start_rownumber=1;
		var end_rownumber=0;
		start_rownumber = step*10000-9999;
		end_rownumber=step*10000;
		var sKind		= "getOCAZ400C_tab13_"+step;	
		var sMethodName = "service::psmstlib/selectOCAZ400C_tab13.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_OCAZ400C_tab13_"+step+"=ds_OCAZ400C_tab13_"+step;    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " COMPS=" + quote(comps);
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB9=" + quote("PSWRKLIB");
		sArgument += " STEP=" + quote(step);
		sArgument += " F_ROWNUM=" + quote(start_rownumber);
		sArgument += " T_ROWNUM=" + quote(end_rownumber);
		sArgument += " TOFILE=" + quote("F#4501R");
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
	}
	//Dataset 추가
	for(step=1 ;step<=cntStep;step++){
		if (object("ds_OCAZ400C_tab13_"+step).rowcount > 0) ds_OCAZ400C_tab13.AppendDataset("ds_OCAZ400C_tab13_"+step);
	}
}

function Tab0_tab13_btnExcel_OnClick(obj)
{
	if (ds_OCAZ400C_tab13.rowcount < 1){
		alert("저장할 Data가 없습니다.!");
		return;
	}
	alert("VDI환경에서는 저장 경로를 c:\~ 선택하세요!");
	FileDialog0.Type = "DIR";
	if (!FileDialog0.Open()) {
		alert("저장 경로를 선택하세요!");
		return;
	}
	var fName   = substr(getDate(), 0, 8)+"_F#4501R(품명업체명추가).xlsx"; 
	var getLen=length(FileDialog0.FilePath);
	if(substr(FileDialog0.FilePath,getLen-1,1)!="\\"){
		fName= "\\"+fName;
	}
	var ExportObject1;			
	ExportObject1 = CreateExportObject();
	ExportObject1.ExportType		= "Excel";
	ExportObject1.ExportFileName	= FileDialog0.FilePath + fName;  //"c:\\test1111.xlsx";
	ExportObject1.ActiveSheetName   = "F#4501R(품명업체명추가)";   //"test";
	ExportObject1.MakeEmptyFileWhenNotExist = true;	
	ExportObject1.AddExportGrid("F#4501R(품명업체명추가)" + "!" + "A1", Tab0.tab13.MainGrid, true, false);
	ExportObject1.Export(true, false); // 첫번째 progress, 두번째 excel
	ExportObject1.Save(); // <--------- 이 부분 추가해주세요 .
	ExportObject1.CloseWorkbook(); // <--------- 이 부분 추가해주세요 .
	ExportObject1.Close();
	ExportObject1 = null;

	alert(FileDialog0.FilePath + fName+"으로 저장되었습니다.");


	/* SaveExcelEx(strFileName,strDocName,strPassword,bAppOpen,strCell,bSelOnly,bAddHeadSumm,bAsync,bMerge);
		strFilename - file명
		strdocname - sheet명
		strCell    - 시작셀을 지정 : 생략가능(default a1)
		bSelOnly   - 선택된 범위만 : 생략가능(default false)
		baddhead   - 헤더표시여부  :생략가능(default true)
	*/
	//Tab0.tab13.MainGrid.SaveExcelEx(FileDialog0.FilePath + fName_xls, "F#4501R(품명업체명추가)", "", false, "A1", false, false, false);

	/* ext_Ds2CsvEx(strDsname,strFileName,Quoteyn, headyn,columnGbn,newlinestr,idbMark,strFileFormat)
		strDsname,   : dataset id
		strFileName, : 저장할 filename(파일명이 없으면 return값으로 줌)
		Quoteyn,     : 따옴표 사용여부(default 1)
		headyn,      : head 출력여부(default 1)
		columnGbn,   : column분리자 (default comma)
		newlinestr,  : newLine으로 사용할 문자(default \r\n)
		idbMark,     : 쌍따음표 사용 여부( 1 : 사용 / 0 : 미사용)
		strFileFormat :“unicode”인 경우 unicode 파일로 저장됨.  “UTF-8”인 경우  UTF-8 저장.
	*/
	//ext_Ds2CsvEx("ds_OCAZ400C_tab13",FileDialog0.FilePath + fName,1,1,",","\r\n",0,"unicode");
	//alert(FileDialog0.FilePath + fName+"으로 저장되었습니다.");		
}
function minusExpr(strF){
	//iif(right(F09,1)=="-","-"+replace(trim(F09),"-",""),trim(F09))    
	var retStr = iif(right(strF,1)=="-","-"+replace(trim(strF),"-",""),trim(strF));
	return retStr;
}
