//버튼 Obj 획득
function btnClick(obj)
{
	//작업자생성
	if(Div1.rdoggub_TYPE.Value =="T"){
		if(substr(obj.id,0,3)!="WRK"){	//라인OBJ인경우만 실행
			var objChk = obj;
			var p_OBJID=obj.id;
			var p_Row=ds_OBMZ230Q_A.FindRow("OBJID",p_OBJID);
			//trace(p_OBJID+"==="+p_Row);

			var p_obj, p_name, p_pos_x, p_pos_y, p_width, p_height, p_Color, p_font;
				p_SEQ=parseInt(ds_OBMZ220.GetColumn(ds_OBMZ220.lastrow,"OSEQ"))+1;	//오브젝트 ID

				p_name=ds_OBMZ230Q_A.GetColumn(p_Row,"LINCD");	//라인코드
				p_pos_x=ds_OBMZ230Q_A.GetColumn(p_Row,"POS_X");	//X좌표
				p_pos_y=ds_OBMZ230Q_A.GetColumn(p_Row,"POS_Y");	//Y좌표
				p_width=40;	//WIDTH
				p_height=28;	//HEIGHT
				p_Color = "Blue";
	/*
			var rtn = dialog("psogmlib::OBMZ220_POP.xml","doAction= new "+ "p_FSIZE="+p_FSIZE+" "+ "p_POSX="+p_POSX+" "+ "p_POSY="+p_POSY+" "+
							"p_WIDTH="+p_WIDTH+" "+ "p_HEIGHT="+p_HEIGHT+" "+ "p_TMPOS="+p_TMPOS+" " ,300,300,"TitleBar=false");				

			var rtn = dialog("psogmlib::OBMZ220_POP.xml","doAction= mod "+ "p_OBJID="+p_OBJID+" "+ "p_Row="+p_Row+" ",300,300,"TitleBar=false");
	*/
			var sTemp="zzzzzzzzzz";
			var selectnumber = "Q";
			var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp+ " " + "P_COMPS=" + ComboBoxPlant.Value,584,528);
			if (length(rtn) != 0){		
				//DATASET에 버튼정보저장
				ds_OBMZ220.AddRow();
				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"COMPS",ComboBoxPlant.Value);	//사업부
				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"OBJID","WRK_"+p_SEQ);	//오브젝트id
				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"OTYPE","W");	//종류
				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"OSEQ",p_SEQ);	//순서


				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"LINCD",ds_OBMZ230Q_A.GetColumn(p_Row,"LINCD"));	//라인코드

				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"OTEXT",rtn[2]);	//작업자코드

				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"OWIDTH",40);	//넓이
				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"OHEIGHT",28);	//높이
				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"POS_X",ds_OBMZ230Q_A.GetColumn(p_Row,"POS_X"));
				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"POS_Y",ds_OBMZ230Q_A.GetColumn(p_Row,"POS_Y"));

				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"ERYMD","");
				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"ERSEQ",0);
				ds_OBMZ220.SetColumn(ds_OBMZ220.currow,"SABUN",rtn[0]);

			//trace(ds_OBMZ220.SaveXML());
				var nDrwaing	= "left='"+p_pos_x+"' top='"+p_pos_y+"' width='"+p_width+"' height='"+p_height+"' WordWrap=true Align=Center VAlign=Middle Color=White "+" text='"+rtn[2]+"' BKColor='"+p_Color+"' Font='"+p_Font1+p_font+p_Font2+"' Appearance=Flat onclick='btnClick'";
				object("Div_LAYOUT").Create("Button","WRK_"+p_SEQ, nDrwaing);
			}			
		}
	}
	//이동처리
	if(Div1.rdoggub_TYPE.Value =="S"){	
		if(substr(obj.id,0,3)=="WRK"){	//작업자 OBJ만 이동 가능하도록
		//trace(obj.id);	
			for(i=0; i<ds_OBMZ230Q_A.rowcount; i++){
				if(ds_OBMZ230Q_A.GetColumn(i,"OTYPE")=="L"){
					var p_objNm=ds_OBMZ230Q_A.GetColumn(i,"OBJID");
					var p_obj2=Div_LAYOUT.object(p_objNm);	//오브젝트 ID
						p_obj2.Enable=false;
				}
			}

			//오리지날 DATA SEt
			if(preChk != "F"){
				preObj.BKColor =preBkColor;
				preObj="";	//초기화
				preChk="F";	//초기화
			}else{
				preObj=obj;	//OBJECT 저장
				preBkColor=obj.BKColor;	//OBJECT Color저장
				obj.BKColor ="yellow";	//선택 Object Color 변경
				preChk="T"; //변경여부 Flag
			}
		}
	}


	//삭제처리 - 보전의뢰관리PGM에서 등록이 안된 작업자만 삭제가능
	if(Div1.rdoggub_TYPE.Value =="D"){
		//trace(obj.id);
		//DATASET 삭제처리
		var getRow=ds_OBMZ220.FindRow("OBJID",obj.id);
		if(length(trim(ds_OBMZ220.GetColumn(getRow,"ERYMD")))!=0){
			alert("[알림]실제배치된 작업자로 삭제가 불가능합니다.");
			return;
		}
		ds_OBMZ220.DeleteRow(getRow);
		//trace(ds_OBMZ220.SaveXML());
		//화면에서 삭제
		Div_LAYOUT.Destroy(obj.id);
	}
}


function Div_LAYOUT_OnMouseDown(obj,nButton,nX,nY)
{
//이동-test용
	if(Div1.rdoggub_TYPE.Value ="S" ){
		if(nButton=0){
			if(isValidObject(preObj)){
				preObj.Left =nX;
				preObj.Top = nY;
				var p_Row=ds_OBMZ220.FindRow("OBJID",preObj.id);
				//trace(p_Row);
			//trace(ds_OBMZ220.SaveXML());
				ds_OBMZ220.SetColumn(p_Row,"POS_X",nX);	//X좌표
				ds_OBMZ220.SetColumn(p_Row,"POS_Y",nY);	//Y좌표
			//trace(ds_OBMZ220.SaveXML());
			}
		}
	}
}
function Div1_rdoggub_TYPE_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(Div1.rdoggub_TYPE.Value ="T"){
		if(preChk != "F"){
			preObj.BKColor =preBkColor;
			preObj="";	//초기화
			preChk="F";	//초기화
		}
		for(i=0; i<ds_OBMZ230Q_A.rowcount; i++){
			if(ds_OBMZ230Q_A.GetColumn(i,"OTYPE")=="L"){
				var p_objNm=ds_OBMZ230Q_A.GetColumn(i,"OBJID");
				var p_obj2=Div_LAYOUT.object(p_objNm);	//오브젝트 ID
					p_obj2.Enable=true;
			}
		}
	}else {
		if(preChk != "F"){
			preObj.BKColor =preBkColor;
			preObj="";	//초기화
			preChk="F";	//초기화
		}
		for(i=0; i<ds_OBMZ230Q_A.rowcount; i++){
			if(ds_OBMZ230Q_A.GetColumn(i,"OTYPE")=="L"){
				var p_objNm=ds_OBMZ230Q_A.GetColumn(i,"OBJID");
				var p_obj2=Div_LAYOUT.object(p_objNm);	//오브젝트 ID
					p_obj2.Enable=false;
			}
		}
	}
}

function fac_layout(){
	var p_obj, p_name, p_pos_x, p_pos_y, p_width, p_height, p_Color, p_font;
	var	sdrawing;

	if(ds_OBMZ220.rowcount>0){
		for(i=0; i<ds_OBMZ220.rowcount; i++){
			Div_LAYOUT.Destroy(trim(ds_OBMZ220.GetColumn(i,"OBJID"))); //작업자 오브젝트삭제
		}
	}

	//공장Layout생성
	for(i=0; i<ds_OBMZ230Q_A.rowcount; i++){
		p_obj=ds_OBMZ230Q_A.GetColumn(i,"OBJID");	//오브젝트 ID
		p_name=ds_OBMZ230Q_A.GetColumn(i,"OTEXT");	//라인명
		p_pos_x=ds_OBMZ230Q_A.GetColumn(i,"POS_X");	//X좌표
		p_pos_y=ds_OBMZ230Q_A.GetColumn(i,"POS_Y");	//Y좌표
		p_width=ds_OBMZ230Q_A.GetColumn(i,"OWIDTH");	//WIDTH
		p_height=ds_OBMZ230Q_A.GetColumn(i,"OHEIGHT");	//HEIGHT
		p_font=ds_OBMZ230Q_A.GetColumn(i,"FSIZE");	//폰트사이즈

		//기본색상
		if(ds_OBMZ230Q_A.GetColumn(i,"OTYPE")=="T"){
			p_Color = "white";
		}else{
			p_Color = "lawngreen";
		}		
		/*
		* function명 : 라인의 상태변경
		* function 주요값 : 라인의 상태(정상: '0', 대기 : 'D' OR 'R', 장애 : 'E')
		* function명 : 설비상태변경
		* function 주요값 : 설비의 상태
		*					(정상: '0', 장애: 'E', 연락중: 'A' 수리중: 'C' 수배중: 'B' 가동중확인: 'H' 품질확인: 'F')
		* function명 : 설비상태변경 가공
		*
						MIN(CASE WHEN STATU='0' THEN 'G' WHEN STATU='E' THEN 'A'
						WHEN STATU='A' THEN 'B' WHEN STATU='C' THEN 'C'
						WHEN STATU='B' THEN 'D'	WHEN STATU='H' THEN 'E'
						WHEN STATU='F' THEN 'F'	ELSE '' END) AS STATU
		*/
		//라인_Status 색상
		if(ds_OBMZ230Q_A.GetColumn(i,"L_STATU")=="0"){//정상
			p_Color = "lawngreen";
		}else if(ds_OBMZ230Q_A.GetColumn(i,"L_STATU")=="R"){//대기
			p_Color = "yellow";
		}else if(ds_OBMZ230Q_A.GetColumn(i,"L_STATU")=="E"){//이상
			p_Color = "orange";
		}
		//2시간미만
		var p_INTTM=ds_OBMZ230Q_A.GetColumn(i,"M_INTTM");
		if(parseInt(p_INTTM) > 200){//2시간미만
			p_Color = "red";
		}
		//설비_Status 색상
		if(ds_OBMZ230Q_A.GetColumn(i,"M_STATU")=="E"){//가동중확인 H->E
			p_Color = "paleturquoise";
		}else if(ds_OBMZ230Q_A.GetColumn(i,"M_STATU")=="F"){//품질확인  F->F
			p_Color = "palegoldenrod";
		}

		if(ds_OBMZ230Q_A.GetColumn(i,"OTYPE")=="T"){
			sdrawing	= "left='"+p_pos_x+"' top='"+p_pos_y+"' width='"+p_width+"' height='"+p_height+"' WordWrap=true Align=Center VAlign=Middle text='"+p_name+"' BKColor='"+p_Color+"' Font='"+p_Font1+p_font+p_Font2+"' Border=Flat ";
			object("Div_LAYOUT").Create("Static",p_obj , sdrawing);
		}else{
			sdrawing	= "left='"+p_pos_x+"' top='"+p_pos_y+"' width='"+p_width+"' height='"+p_height+"' WordWrap=true"+" text='"+p_name+"' BKColor='"+p_Color+"' Font='"+p_Font1+p_font+p_Font2+"' Appearance=Flat onclick='btnClick' Enable=false";
			object("Div_LAYOUT").Create("Button",p_obj , sdrawing);
		}
		//trace(p_obj);
	}

	//작업자생성
	for(i=0; i<ds_OBMZ230Q_B.rowcount; i++){
		p_obj=trim(ds_OBMZ230Q_B.GetColumn(i,"OBJID"));	//오브젝트 ID
		p_name=ds_OBMZ230Q_B.GetColumn(i,"WRKNM");	//라인명
		p_pos_x=ds_OBMZ230Q_B.GetColumn(i,"POS_X");	//X좌표
		p_pos_y=ds_OBMZ230Q_B.GetColumn(i,"POS_Y");	//Y좌표
		p_width=ds_OBMZ230Q_B.GetColumn(i,"OWIDTH");	//WIDTH
		p_height=ds_OBMZ230Q_B.GetColumn(i,"OHEIGHT");	//HEIGHT

		if(length(p_obj) != 0){
			//작업자라벨
			sdrawing	= "left='"+parseInt(p_pos_x)+"' top='"+p_pos_y+"' width='"+p_width+"' height='"+p_height+"' WordWrap=true Align=Center VAlign=Middle Color=White BKColor=Blue text='"+p_name+"' Font='"+p_Font1+p_font+p_Font2+"' Appearance=Flat onclick='btnClick'";
			object("Div_LAYOUT").Create("Button",p_obj , sdrawing);
		}
	}
	getOBMZ220(); //작업자배치리딩
}
