/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getOBNZ110(){

	if(!validationChk()){
		return;
	}

	var sKind		= "getOBNZ110";						
	var sMethodName = "service::psogmlib/selectOBNZ110.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OBNZ110=ds_OBNZ110";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += "@LINCD=" + quote(cmb_LINCD.Value);	//라인코드
		sArgument += "@MSHNO=" + quote(cmb_MSHNO.Value);	//설버No
	var str = dialog("Common::PGMLOAD_OPG.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = false;

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	chkCNT=ds_OBNZ110.rowcount;
	st_RDATE=ds_OBNZ110.GetColumn(0,"RDATE");
	Edit0.Text=chkCNT;
}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 * 			
 **********************************************************************/ 

 function setOBNZ110(){

	if(chK_CHGCD.Value=true){
		if(cmb_CHGDT.Value <= st_RDATE.Value){
			cmb_CHGDT.BKColor="paleturquoise";
			alert("[경고]생성일자가 최종개정일보다 작을 수 없습니다.");
			return;
		}
	}

	var sKind		= "setOBNZ110";						
	var sMethodName = "service::psogmlib/saveOBNZ110.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_OBNZ110=ds_OBNZ110:U";                      // 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OBNZ110_CHECK=ds_OBNZ110_CHECK";							// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		//sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " USER_ID=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
	//제정 및 개정시 날짜
		sArgument += " CHGCD=" + quote(chK_CHGCD.Value);	//제정 개정 유무
	if(chK_CHGCD.Value=true){
		sInDataSet  = "ds_OBNZ110=ds_OBNZ110:A";
		if(chkCNT=0){	//제정
			sArgument += " DDATE=" + quote(cmb_CHGDT.Value);	//제정일자
			sArgument += " RDATE=" + quote(cmb_CHGDT.Value);	//개정일자
			sArgument += " P_CNT=" + quote(chkCNT);	//건수
		}else{	//개정
			sArgument += " DDATE=" + quote(ds_OBNZ110.GetColumn(0,"DDATE"));	//제정일자
			sArgument += " RDATE=" + quote(cmb_CHGDT.Value);	//개정일자
			sArgument += " P_CNT=" + quote(cmb_CHGDT.Value);	//건수	
		}
	}
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;
 }
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if(svcId=="setOBNZ110"){
		if(rtnCd<0){
			alert(setPopMessage("1002"));
			setMessage("1002");

		}else{
			if(ds_OBNZ110.rowcount<1){
				alert(setPopMessage("1003"));
				setMessage("1003");	
			}else{
				alert(setPopMessage("1004"));
				setMessage("1004");	
			}
		}
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){
	var result = true;
	cmb_MSHNO.BKColor="white";
	cmb_CHGDT.BKColor="white";

	if (cmb_MSHNO.Value == ""){
		cmb_MSHNO.BKColor="paleturquoise";
		alert("[알림]설비No를 입력해주세요.");
		//alert(setPopMessage("4053"));//[확인] 기준년월을 확인바랍니다.
		//setMessage("4053");
		result = false;
	}
	else if(chK_CHGCD.Value=true){
		if(cmb_CHGDT.Value == ""){
			cmb_CHGDT.BKColor="paleturquoise";
			alert("[알림]생성일자를 입력해주세요.");
			//alert(setPopMessage("4053"));//[확인] 기준년월을 확인바랍니다.
			//setMessage("4053");
			result = false;
		}
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "점검체크리스트";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|점검체크리스트";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

