function fn_Search()
{
	getOBNZ110();
}

function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(ds_OBNZ110.rowcount < 1) {
		fn_Search();
		if(ds_OBNZ110.rowcount < 1) {
			if(chK_CHGCD.Value==false){
				alert("신규입력입니다. 제정일자를 입력해주세요.");
				return;
			}
		}

	}

	if(chK_CHGCD.Value==false){
		alert("신규/제정인 경우만 추가가능합니다.");
		return;
	}
	ds_OBNZ110.AddRow();
	ds_OBNZ110.SetColumn(ds_OBNZ110.currow,"COMPS",ComboBoxPlant.Value);
	ds_OBNZ110.SetColumn(ds_OBNZ110.currow,"KYMMD",ComboBoxPlant.Value);//개정년월
	ds_OBNZ110.SetColumn(ds_OBNZ110.currow,"LINCD",cmb_LINCD.Value);
	ds_OBNZ110.SetColumn(ds_OBNZ110.currow,"MSHNO",cmb_MSHNO.Value);
	ds_OBNZ110.SetColumn(ds_OBNZ110.currow,"MHOUR",0);//공수
	//ds_OBNZ110.SetColumn(ds_OBNZ110.currow,"PYEAR",substr(cmb_PYEAR.Value,0,4));
}

function fn_Delete()
{
	if(ds_OBNZ110.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_OBNZ110.DeleteRow(ds_OBNZ110.currow);	//삭제		
	alert(setPopMessage("1011"));	
	setPopMessage("1011");
}

