function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
}


function cmb_LINCD_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_OBNZ110.ClearData();
	cmb_MSHNO_OnClick();
	cmb_MSHNO.SetFocus();
	cmb_MSHNO.Index=1;
}

function cmb_MSHNO_OnClick(obj)
{
	ds_MSHNO.Filter("LINCD=='" + cmb_LINCD.Value + "'" );
	ds_OBNZ110.ClearData();
}

function ds_OBNZ110_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if (strColumnID == "MHOUR"){//공수
		if(length(trim(varNewValue))==0){
			ds_OBNZ110.SetColumn(nRow,"MHOUR",0);
		}
	}
}

function chK_CHGCD_OnClick(obj,strValue)
{
	ds_OBNZ110.ClearData();

	if(chK_CHGCD.Value==true){
		cmb_CHGDT.Enable=true;
	}else{
		cmb_CHGDT.Enable=false;
	}
}

function rdoggub_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(rdoggub.Value=="T"){
		MainGrid.Visible=true;
		MainGrid2.Visible=false;
	}else{
		MainGrid.Visible=false;
		MainGrid2.Visible=true;
	}
}
