var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
//보전이력 날짜조회시 2016년도 인경우 201601~201612임

function on_LoadCompleted(obj){
	getTitle();
	getDesign();
	getComCode();	
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	getPRDCD();	//제품명조회

	cmb_KNYYM_FR.Value=substr(cmb_KNYYM_FR.Value,0,6)+"01";
	cmb_KNYYM_TO.Value=addDate(left(addMonth(getDate(),1),6) + "01",-1);
}

function getComCode(){

	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 	
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	//MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	//MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");  //22
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");            //1048
	//MainGrid.Top = G_ds_design.GetColumn(0,"Top");                //64
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");           //1048
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	          //32
	//searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	      //29
}

/********************************************************************** 
 * 내용설명 : 제품코드 조회
 **********************************************************************/ 
function getPRDCD(){
	var sKind		= "getPRDCD";						
	var sMethodName = "service::psogmlib/selectMSTCODE.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_PRDCD=ds_MSTCODE";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //코드
		sArgument += " SQLID=" + "selectMSTCD";	//알람/전라인의 마스터 코드로 사용
		sArgument += " GRPCD=" +  quote("PRD");	//제품코드
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	ds_PRDCD.InsertRow(0);
	ds_PRDCD.SetColumn(0,"CD","");
	ds_PRDCD.SetColumn(0,"CDNM","전체");
	ds_PRDCD.SetColumn(0,"CDNM2","전체");
	cmb_PRDCD.Index=0;
}
/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getOBNZ250Q(){

	if(!validationChk()){
		return;
	}
	ds_OBNZ250Q_SUB.ClearData();
	ds_OBNZ250Q.ClearData();

	var sKind		= "getOBNZ250Q";						
	var sMethodName = "service::psogmlib/selectOBNZ250Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_OBNZ250Q_A=ds_OBNZ250Q_A ds_OBNZ250Q_B=ds_OBNZ250Q_B";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		//sArgument += " KNYYM=" + quote(substr(cmb_KNYYM.Value,0,6));	//계획보전년월
		sArgument += " KNYYM_FR=" + quote(cmb_KNYYM_FR.Value);	//계획보전년월(FR)
		sArgument += " KNYYM_TO=" + quote(cmb_KNYYM_TO.Value);	//계획보전년월(TO)		
		sArgument += " PRDCD=" + quote(cmb_PRDCD.Value);	//제품코드
	//var str = dialog("Common::PGMLOAD_OPG.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if(svcId=="getOBNZ250Q"){
		if(rtnCd<0){
			alert(setPopMessage("1002"));
			setMessage("1002");
		}else{
			var r_Cnt=ds_OBNZ250Q_B.rowcount;
			if(ds_OBNZ250Q_A.rowcount > ds_OBNZ250Q_B.rowcount){
				r_Cnt=ds_OBNZ250Q_A.rowcount;
			}
			if(r_Cnt < 1 ){
				alert(setPopMessage("1003"));
				setMessage("1003");
			}else{
				for(i=0; i<r_Cnt; i++){
					ds_OBNZ250Q_SUB.InsertRow(i);
					ds_OBNZ250Q_SUB.SetColumn(i, "LINNM", ds_OBNZ250Q_A.GetColumn(i,"LINNM"));
					ds_OBNZ250Q_SUB.SetColumn(i, "L_SUM", ds_OBNZ250Q_A.GetColumn(i,"SUM_TOTHR"));
					ds_OBNZ250Q_SUB.SetColumn(i, "L_CNT", ds_OBNZ250Q_A.GetColumn(i,"CNT_TOTHR"));
					ds_OBNZ250Q_SUB.SetColumn(i, "L_STP", ds_OBNZ250Q_A.GetColumn(i,"SUM_STPHR"));
					ds_OBNZ250Q_SUB.SetColumn(i, "L_REG", ds_OBNZ250Q_A.GetColumn(i,"CNT_REG"));
					ds_OBNZ250Q_SUB.SetColumn(i, "L_CMP", ds_OBNZ250Q_A.GetColumn(i,"CNT_CMP"));
					ds_OBNZ250Q_SUB.SetColumn(i, "MSHNM", ds_OBNZ250Q_B.GetColumn(i,"MSHNM"));
					ds_OBNZ250Q_SUB.SetColumn(i, "M_SUM", ds_OBNZ250Q_B.GetColumn(i,"SUM_TOTHR"));
					ds_OBNZ250Q_SUB.SetColumn(i, "M_CNT", ds_OBNZ250Q_B.GetColumn(i,"CNT_TOTHR"));
					ds_OBNZ250Q_SUB.SetColumn(i, "M_STP", ds_OBNZ250Q_B.GetColumn(i,"SUM_STPHR"));
					ds_OBNZ250Q_SUB.SetColumn(i, "M_REG", ds_OBNZ250Q_B.GetColumn(i,"CNT_REG"));
					ds_OBNZ250Q_SUB.SetColumn(i, "M_CMP", ds_OBNZ250Q_B.GetColumn(i,"CNT_CMP"));
				}
				ds_OBNZ250Q.Copy(ds_OBNZ250Q_SUB);
				ds_OBNZ250Q.SaveXML();
				alert(setPopMessage("1004"));
				setMessage("1004");	
			}
		}
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function validationChk(){
	var result = true;
	cmb_KNYYM_FR.BKColor="white";
	cmb_KNYYM_TO.BKColor="white";

	if(cmb_KNYYM_FR.Value == ""){
		cmb_KNYYM_FR.BKColor="paleturquoise";
		alert(setPopMessage("6025"));//[알림]시작일자를 입력하세요.
		setMessage("6025");
		result = false;
	}else if(cmb_KNYYM_TO.Value == ""){
		cmb_KNYYM_TO.BKColor="paleturquoise";
		alert(setPopMessage("6026"));//[알림]종료일자를 입력하세요.
		setMessage("6026");
		result = false;
	}else if(cmb_KNYYM_FR.Value > cmb_KNYYM_TO.Value){
		cmb_KNYYM_FR.BKColor="paleturquoise";
		alert(setPopMessage("6029"));//[알림]일자를 확인하세요.
		setMessage("6029");
		result = false;
	}
	return result;
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "주요설비 고장실적";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|주요설비 고장실적";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

