/********************************************************************** 
 * 개 발 자 : 김정현 
 * 개발일자 : 2012-10-26
 * 내용설명 : validation 체크
 * 주요변수 : 
 **********************************************************************/ 
function validationChk(){
	for(var i=0; i<DS_OBSE04_Tab2.rowcount(); i++){

		if(length(trim(DS_OBSE04_Tab2.GetColumn(i, "PSPNO")))==0){
			alert("품번을 입력하세요!");	
			DS_OBSE04_Tab2.row = i;
			return false;
		}
		if(length(trim(DS_OBSE04_Tab2.GetColumn(i, "badqy")))==0){
			alert("불량수를 입력하세요!");	
			DS_OBSE04_Tab2.row = i;
			return false;
		}			
		if(length(trim(DS_OBSE04_Tab2.GetColumn(i, "badtm")))==0){
			alert("불량발생 시간을 입력하세요!");	
			DS_OBSE04_Tab2.row = i;
			return false;
		}
		if(length(trim(DS_OBSE04_Tab2.GetColumn(i, "badnm")))==0){
			alert("발생사유를 입력하세요!");	
			DS_OBSE04_Tab2.row = i;
			return false;
		}		
	}

	return true;
}


function onRowPosChange(obj,nOldRow,nRow)
{
	if(ds_ppmi006mTab1.GetRowType(ds_ppmi006mTab1.row) == "insert"){
		cd_daygu.Enable = true;
		Edit2.Enable = true;
		Edit1.Enable = true;	
	} else {
		cd_daygu.Enable = false;
		Edit2.Enable = false;
		Edit1.Enable = false;	
	}

}

