function img_cd_OnClick(obj,nX,nY)
{
	var rtn = dialog("psogmlib::OBSE05_FIND.xml","TAB=2",584,528);

	if (length(rtn) != 0){						
		ed_lg.Text = rtn[0];
		ed_md.Text = rtn[1];
		ed_sm.Text = rtn[2];		
		ed_cd.Text = rtn[3];
	}	
}

function btn_insert_OnClick(obj)
{		
	//검색시 그 결과값이 없을 때 추가ㅣ버튼을 누르면 로우가 추가되지 않기에 강제추가
	if(DS_OBSE04_Tab2.GetRowCount() < 1){	
		DS_OBSE04_Tab2.AddColumn("COMPS");
		DS_OBSE04_Tab2.AddColumn("DAYGU");
		DS_OBSE04_Tab2.AddColumn("BADLG");
		DS_OBSE04_Tab2.AddColumn("ORDAT");
		DS_OBSE04_Tab2.AddColumn("PROCS");
		DS_OBSE04_Tab2.AddColumn("BADLG");
		DS_OBSE04_Tab2.AddColumn("BADMD");
		DS_OBSE04_Tab2.AddColumn("BADSM");
		DS_OBSE04_Tab2.AddColumn("BADLG_NAME");		
		DS_OBSE04_Tab2.AddColumn("BADMD_NAME");
		DS_OBSE04_Tab2.AddColumn("BADSM_NAME");		
		DS_OBSE04_Tab2.AddColumn("PSPNO");
		DS_OBSE04_Tab2.AddColumn("BADQY");
		DS_OBSE04_Tab2.AddColumn("BADTM");		
		DS_OBSE04_Tab2.AddColumn("BADNM");		
		DS_OBSE04_Tab2.AddColumn("MSTRM");
	} 

	DS_OBSE04_Tab2.AddRow();
}

function btn_save_OnClick(obj)
{
	if(!validationChk()) return;

	var sKind		= "saveOBSE05_Tab3";						
	var sMethodName = "service::psogmlib/saveOBSE05_Tab3.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "DS_OBSE04_Tab2=DS_OBSE04_Tab2:U";  				//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     			// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " PROCS=" + quote(procs);
	sArgument += " ORDAT=" + quote(ordat);		

	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;	

	getOBSE05Tab3();
}

function btn_del_OnClick(obj)
{
	DS_OBSE04_Tab2.DeleteRow(DS_OBSE04_Tab2.currow);

	var sKind		= "saveOBSE05_Tab1_Bottom";						
	var sMethodName = "service::psogmlib/saveOBSE05_Tab3.do";   // 서버에서 호출 될 Method 명
	var sInDataSet  = "DS_OBSE04_Tab2=DS_OBSE04_Tab2:U";   		//자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     	// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += " PROCS=" + quote(procs);
	sArgument += " ORDAT=" + quote(ordat);		

	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync=false;	
}


function DS_OBSE04_Tab2_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if (DS_OBSE04_Tab2.rowcount > 0) {
		ed_cd.Text = DS_OBSE04_Tab2.GetColumn(DS_OBSE04_Tab2.currow,"BADLG_NAME") + " / " + DS_OBSE04_Tab2.GetColumn(DS_OBSE04_Tab2.currow,"BADMD_NAME") + " / " + DS_OBSE04_Tab2.GetColumn(DS_OBSE04_Tab2.currow,"BADSM_NAME");
	} else {
		ed_cd.Text = "";
	}		
}

function DS_OBSE04_Tab2_OnRowPosChanged(obj,nOldRow,nRow)
{
	if (DS_OBSE04_Tab2.rowcount > 0) {
		ed_cd.Text = DS_OBSE04_Tab2.GetColumn(DS_OBSE04_Tab2.currow,"BADLG_NAME") + " / " + DS_OBSE04_Tab2.GetColumn(DS_OBSE04_Tab2.currow,"BADMD_NAME") + " / " + DS_OBSE04_Tab2.GetColumn(DS_OBSE04_Tab2.currow,"BADSM_NAME");
	} else {
		ed_cd.Text = "";
	}		
}
