function on_LoadCompleted(obj){
	getTitle();	
	setClass(ComboBoxClass,ds_Class); //분류지정(1:예비품, 2:비품, 3:식자재)
	ComboBoxClass.Value='1';
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
}
function selectClassCode()
{
	var cbx = ComboBoxClass.Value;
	SetVariable("ClassGB",cbx);
}

function Image0_OnClick(obj,nX,nY)
{
	selectClassCode();
}

function menu_OnFocus(obj)
{
