function getComCode(){
	http.Sync=true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");
}


function getogm080B(){
	if (length(cmb_Code.Value) == 0) {
		alert(setPopMessage("2532"));
		setMessage("2532");
		return; 
	}
	if(cmb_Code.Text != 0 ){
		switch (substr(cmb_code.Value,0,8)){
			case "obc190pf" : MainGrid.SetCellProp("head",1,"text","분류CD");
			case "obc190pf" : MainGrid.SetCellProp("head",2,"text","현상분류");
			case "obc190pf" : Div0.Grid0.SetCellProp("head",1,"text","현상코드"); 
			case "obc190pf" : Div0.Grid0.SetCellProp("head",2,"text","현상"); break;
			case "obc120pf" : MainGrid.SetCellProp("head",1,"text","분류CD"); 
			case "obc120pf" : MainGrid.SetCellProp("head",2,"text","원인분류");
			case "obc120pf" : Div0.Grid0.SetCellProp("head",1,"text","원인코드"); 
			case "obc120pf" : Div0.Grid0.SetCellProp("head",2,"text","원인"); break;
			case "obc100pf" : MainGrid.SetCellProp("head",1,"text","분류CD"); 
			case "obc100pf" : MainGrid.SetCellProp("head",2,"text","실원인분류"); 
			case "obc100pf" : Div0.Grid0.SetCellProp("head",1,"text","실원인코드"); 
			case "obc100pf" : Div0.Grid0.SetCellProp("head",2,"text","실원인"); break;
			case "obc160pf" : MainGrid.SetCellProp("head",1,"text","처치CD"); 
			case "obc160pf" : MainGrid.SetCellProp("head",2,"text","처치"); break;
		}
	}else{
		//ed_mstcd1.Enable=false;
	}
	var tableNm = substr(cmb_Code.Value,0,8);
	var field1 = substr(cmb_Code.Value,9,5);
	var field2 = substr(cmb_Code.Value,15,5);
	//var	field3 = substr(cmb_Code.Value,30,5);
	var chktab = substr(cmb_Code.Value,42,8);
	var sSort = rd_Sort.Value;

	//getogm080B_ALL();

	var sKind		= "getogm080B";
	var sMethodName = "service::psogmlib/selectOGM080B.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ogm080B=ds_ogm080B";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@TABLENM=" + quote(tableNm); //TABLE명
	sArgument += "@FIELD1=" + quote(field1); //FIELD명1
	sArgument += "@FIELD2=" + quote(field2); //FIELD명2
	//sArgument += "@FIELD3=" + quote(field3); //FIELD명3
	sArgument += "@FGUBUN=" + quote("1"); // 조회할 조건이 (CODE) 문자이면 "1" 숫자이면 "2"
	sArgument += "@FSORT=" + quote(sSort); // 정렬 조건 a: code      b:codenm
	sArgument += "@FCODE=" + quote(trim(ed_Code.Text)); //code
	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	if(ds_ogm080B.GetRowCount() < 1){
		ds_ogm080B.AddColumn("F1");
		ds_ogm080B.AddColumn("F2");
		ds_ogm080B.AddColumn("DELGB");
	}
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setogm080B(){
	if(ds_ogm080B.rowcount() < 1) {
		alert(setPopMessage("2509"));
		setMessage("2509");
		return;
	}
	if(!validationChk()) return;

	var tableNm = substr(cmb_Code.Value,0,8);
	var field1 = substr(cmb_Code.Value,9,5);
	var field2 = substr(cmb_Code.Value,15,5);
	//var field3 = substr(cmb_Code.Value,30,5);
	var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);

	ds_ogm080B.fireEvent = false;
	//for(var z=0;z<ds_ogm090B.rowcount();z++){
	for(var z=ds_ogm080B.rowcount(); z>=0; z--){
		if (ds_ogm080B.GetColumn(z,"DELGB")=="1"){
			ds_ogm080B.DeleteRow(z);
			//alert(setPopMessage("1011"));
			//setMessage("1011");
		}
	}
	ds_ogm080B.fireEvent = true;

	var sKind		= "setogm080B";						
	var sMethodName = "service::psogmlib/saveOGM080B.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ogm080B=ds_ogm080B:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += " TABLENM=" + quote(tableNm); //TABLE명
	sArgument += " FIELD1=" + quote(field1); //FIELD명1
	sArgument += " FIELD2=" + quote(field2); //FIELD명2
	//sArgument += " FIELD3=" + quote(field3); //FIELD명3
	//sArgument += " FGUBUN=" + quote("1"); // 조회할 조건이 (CODE) 문자이면 "1" 숫자이면 "2"
	sArgument += " ADUSR=" + quote(idnum);
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setogm080B"){
		alert(setPopMessage("1001"));
		setMessage("1001");
		//getogm090B();
	}	
}

/********************************************************************** 
 * 내용설명 : validation 체크 추가시
 **********************************************************************/ 
function validationChk(){
if (ds_ogm080B.lastrow>=0){
		if (length(ds_ogm080B.GetColumn(ds_ogm080B.lastrow,"F1")) == 0){
			alert(setPopMessage("2507"));
			setMessage("2507");
			return false;
		}
/*		if (length(ds_ogm080B.GetColumn(ds_ogm080B.lastrow,"F2")) == 0){
			alert(setPopMessage("2534"));
			setMessage("2534");
			return false;
		}*/
	}
	return true;
}

