/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_ogm080B_OnRowPosChanged(obj,nOldRow,nRow){
	//하단 코드 조회
	if(cmb_Code.Text == "처치"){
		Div0.Visible = false;
		return;
	}else{
		Div0.Visible = true;
	}
	if(ds_ogm080B.GetRowType(ds_ogm080B.currow)=="insert"){
		ds_ogm080B_B.cleardata();
	}else{
		var tableNm_B = substr(cmb_Code.Value,21,8);
		var field1_B = substr(cmb_Code.Value,30,5);
		var field2_B = substr(cmb_Code.Value,36,5);
		var field3_B = substr(cmb_Code.Value,9,5);
		//var Code_B = substr(cmb_Code.Value,9,5);
		var Code_B = ds_ogm080B.GetColumn(nRow,"F1");
		//var F1_B = ds_ogm080B.GetColumn(nRow,"F1");
		var sKind		= "getOGM080B_B";						
		var sMethodName = "service::psogmlib/selectOGM080B_B.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_ogm080B_B=ds_ogm080B_B";
		var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " TABLENM_B=" + quote(tableNm_B); //TABLE명
			sArgument += " FIELD1_B=" + quote(field1_B); //FIELD명1
			sArgument += " FIELD2_B=" + quote(field2_B); //FIELD명2
			sArgument += " FIELD3_B=" + quote(field3_B); //FIELD명3
			sArgument += " FCODE_B=" + quote(Code_B); //라인코드
		http.Sync 	= true;	
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync 	= false;
		//alert("OK");
		if(ds_ogm080B_B.GetRowCount() < 1){
			ds_ogm080B_B.AddColumn("F1_B");
			ds_ogm080B_B.AddColumn("F2_B");
			ds_ogm080B_B.AddColumn("DELGB");
		}
	}

	//로우타입 insert시 setting
	if(ds_ogm080B.GetRowType(ds_ogm080B.currow)=="insert"){
		switch (substr(cmb_code.Value,0,8)){
			case "obc190pf" : MainGrid.SetCellProp("body",1,"limit","5"); break;
			case "obc120pf" : MainGrid.SetCellProp("body",1,"limit","5"); break;
			case "obc100pf" : MainGrid.SetCellProp("body",1,"limit","5"); break;
			case "obc160pf" : MainGrid.SetCellProp("body",1,"limit","5"); break;
		}
	}else{
		//ed_mstcd1.Enable=false;
	}
}
