 * 내용설명 : validation 체크 하단
 **********************************************************************/ 
function ds_ogm080B_B_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if( strColumnID == "F1_B" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2536"));
			setMessage("2536");
			return false;
		}
		//중복check
		var tableNm_B = substr(cmb_Code.Value,21,8);
		var field1_B = substr(cmb_Code.Value,30,5);
		var sKind		= "getogm080B_B_Chk";
		var sMethodName = "service::psogmlib/selectOGM080B_B_Chk.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_ogm080B_B_Chk=ds_ogm080B_B_Chk";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " TABLENM_B=" + quote(tableNm_B); //TABLE명
		sArgument += " FIELD1_B=" + quote(field1_B); //FIELD명1
		sArgument += " FVALUE=" + quote(varNewValue); //FIELD명1
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_ogm080B_B_Chk.GetRowCount() != 0){
			alert(setPopMessage("2533"));
			setMessage("2533");
			return false;
		}

	}

	if(strColumnID == "DELGB" ){
		if (varNewValue == "1"){
			var code = ds_ogm080B_B.GetColumn(nRow,"F1_B");

			var tableNm = substr(cmb_Code.Value,42,8);
			var field1 = substr(cmb_Code.Value,30,5);

			var sKind		= "getogm080B_B_DelChk";
			var sMethodName = "service::psogmlib/selectOGM080B_B_DelChk.do";     		// 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_ogm080B_B_DelChk=ds_ogm080B_B_DelChk";    				// 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " TABLENM=" + quote(tableNm); //TABLE명
			sArgument += " FIELD1=" + quote(field1); //FIELD명1
			sArgument += " CODE=" + quote(code); //FIELD명1
			http.Sync = true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_ogm080B_B_DelChk.GetColumn(0,"F1_B") != "0"){
				alert(setPopMessage("2537"));
				setMessage("2537");
				return false;
			}
		}
	}
	http.usewaitcursor = true;
}

