/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 
function setogm090B(){
	if(ds_ogm090B.rowcount() < 1) {
		alert(setPopMessage("2509"));
		setMessage("2509");
		return;
	}

	ds_ogm090B.fireEvent = false;
	//for(var z=0;z<ds_ogm090B.rowcount();z++){
	for(var z=ds_ogm090B.rowcount(); z>=0; z--){
		if (ds_ogm090B.GetColumn(z,"DELGB")=="1"){
			ds_ogm090B.DeleteRow(z);
		}
	}
	ds_ogm090B.fireEvent = true;

	var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);
	if(!validationChk()) return;
	var sKind		= "setogm090B";						
	var sMethodName = "service::psogmlib/saveOGM090B.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ogm090B=ds_ogm090B:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += " ADUSR=" + quote(idnum);
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setogm090B"){
		alert(setPopMessage("1001"));
		setMessage("1001");
		//getogm090B();
	}	
}

