/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_ogm090B_OnRowPosChanged(obj,nOldRow,nRow){
	//ds_ogm090B_B.DeleteAll();
	//하단 설비번호 조회
	var lincd_B = ds_ogm090B.GetColumn(nRow,"LINCD");
	var sKind		= "getOGM090B_B";						
	var sMethodName = "service::psogmlib/selectOGM090B_B.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm090B_B=ds_ogm090B_B";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부	
		sArgument += " FCODE=" + quote(lincd_B); //라인코드
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	if(ds_ogm090B_B.GetRowCount() < 1){
		ds_ogm090B_B.AddColumn("LINCD");
		ds_ogm090B_B.AddColumn("MSHNO");
		ds_ogm090B_B.AddColumn("MMSHI");
		ds_ogm090B_B.AddColumn("DELGB");
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 
function ds_ogm090B_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if( strColumnID == "LINCD" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2543"));
			setMessage("2543");
			return false;
		}
		//중복check
		/*for(i=0;i<ds_ogm090B_ALL.rowcount; i++){
			if(ds_ogm090B_ALL.GetColumn(i,"LINCD") == toUpper(varNewValue)) {
				alert("기존 등록된 라인코드입니다.");
				return false;
			}
		}*/
		var sKind		= "getogm090B_Chk";
		var sMethodName = "service::psogmlib/selectOGM090B_Chk.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_ogm090B_Chk=ds_ogm090B_Chk";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		//sArgument += " TABLENM=" + quote(tableNm); //TABLE명
		//sArgument += " FIELD1=" + quote(field1); //FIELD명1
		sArgument += " FVALUE=" + quote(varNewValue); //FIELD명1
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_ogm090B_Chk.GetRowCount() != 0){
			alert(setPopMessage("2533"));
			setMessage("2533");
			return false;
		}
	}

	if( strColumnID == "CDEPT" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2505"));
			setMessage("2505");
			return false;
		}
	}
	if( strColumnID == "PRTCD" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2544"));
			setMessage("2544");
			return false;
		}
	}
	if( strColumnID == "MLINE" ){
		if(!(trim(varNewValue) == '0' || trim(varNewValue) == '1' )){
			alert(setPopMessage("2545"));
			setMessage("2545");
			return false;
		}
	}
	http.usewaitcursor = true;
}

/********************************************************************** 
 * 내용설명 : DELETE
 **********************************************************************/ 
function ds_ogm090B_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "LINNM" ){
		//alert("start=="+varNewValue);
		var vval=varNewValue; //GRID에 변경된값 
		var vnrow=nRow; //현재 ROW
		var vcid=strColumnID; //컬럼ID
		var lenLimit = MainGrid.GetCellProp("body","2","limit"); //현재 컬럼의 Maxlength
		//alert("strColumnID=="+vcid=+ "   nRow=="+vnrow+"   varNewValue=="+vval+"   lenLimit=="+lenLimit);
		var newVal = chkbyte_db2(vval, lenLimit); //변경된값과 MAxLength을 chkbyte_db2함수에 보내서 바이트 계싼함.
		if (length(newVal)==0){ //리턴받은 값이 0이면 STOP, 아니면 새로운값은 dataset에 set함.
			return;				//처음변경된값 수행 후 바이트된 계산된 값으로 재수행하기 때문에 2회차일경우 방지함.
		}
		//alert("리턴값 : "+newVal);
		//alert(vnrow+"  "+vcid+"  "+newVal);
		varNewValue=newVal;
		ds_ogm090B.SetColumn(vnrow,vcid,varNewValue);
		//ds_ogm080A.ApplyChange();
	//	alert(ds_ogm080A.GetColumn(vnrow,vcid));

	//	ds_ogm080A.SetColumn(nRow,"F2",toUpper(varNewValue));
	}

	if( strColumnID == "CDEPT" ){
		//alert("start=="+varNewValue);
		var vval=varNewValue; //GRID에 변경된값 
		var vnrow=nRow; //현재 ROW
		var vcid=strColumnID; //컬럼ID
		var lenLimit = MainGrid.GetCellProp("body","3","limit"); //현재 컬럼의 Maxlength
		//alert("strColumnID=="+vcid=+ "   nRow=="+vnrow+"   varNewValue=="+vval+"   lenLimit=="+lenLimit);
		var newVal = chkbyte_db2(vval, lenLimit); //변경된값과 MAxLength을 chkbyte_db2함수에 보내서 바이트 계싼함.
		if (length(newVal)==0){ //리턴받은 값이 0이면 STOP, 아니면 새로운값은 dataset에 set함.
			return;				//처음변경된값 수행 후 바이트된 계산된 값으로 재수행하기 때문에 2회차일경우 방지함.
		}
		//alert("리턴값 : "+newVal);
		//alert(vnrow+"  "+vcid+"  "+newVal);
		varNewValue=newVal;
		ds_ogm090B.SetColumn(vnrow,vcid,varNewValue);
		//ds_ogm080A.ApplyChange();
	//	alert(ds_ogm080A.GetColumn(vnrow,vcid));

	//	ds_ogm080A.SetColumn(nRow,"F2",toUpper(varNewValue));
	}

/*
	if( strColumnID == "DELGB" && varNewValue == "1" ){
		//ds_ogm090B.DeleteRow(ds_ogm090B.Row);
		if (ds_ogm090B_B.rowcount() != 0){
			ds_ogm090B.SetColumn(nRow,"DELGB","0");
			alert(setPopMessage("2537"));
			setMessage("2537");
		}
	}
*/
}

/********************************************************************** 
 * 내용설명 : validation 체크 상단
 **********************************************************************/ 
function validationChk(){
	if (ds_ogm090B.lastrow>=0){
		if (length(ds_ogm090B.GetColumn(ds_ogm090B.lastrow,"LINCD")) == 0){
			alert(setPopMessage("2543"));
			setMessage("2543");
			return false;
		}
		if (length(ds_ogm090B.GetColumn(ds_ogm090B.lastrow,"LINNM")) == 0){
			alert(setPopMessage("2546"));
			setMessage("2546");
			return false;
		}
		if (length(ds_ogm090B.GetColumn(ds_ogm090B.lastrow,"CDEPT")) == 0){
			alert(setPopMessage("2505"));
			setMessage("2505");
			return false;
		}
		if (length(ds_ogm090B.GetColumn(ds_ogm090B.lastrow,"PRTCD")) == 0){
			alert(setPopMessage("2542"));
			setMessage("2542");
			return false;
		}
		if (length(ds_ogm090B.GetColumn(ds_ogm090B.lastrow,"MLINE")) == 0){
			ds_ogm090B.SetColumn(ds_ogm090B.lastrow,"MLINE","0");
		}
	}
	return true;
}



