/********************************************************************** 
 * 내용설명 : 라인코드설비리스트 저장
 **********************************************************************/ 
function Div0_btnSave_OnClick(obj)
{
	if(ds_ogm090B_B.rowcount() < 1) {
		alert(setPopMessage("2547"));
		setMessage("2547");
		return;
	}
	var LineCD = Div0.st_lincd.Text;

	ds_ogm090B_B.fireEvent = false;
	//for(var z=0;z<ds_ogm090B.rowcount();z++){
	for(var z=ds_ogm090B_B.rowcount(); z>=0; z--){
		if (ds_ogm090B_B.GetColumn(z,"DELGB")=="1"){
			ds_ogm090B_B.DeleteRow(z);
		}
	}
	ds_ogm090B_B.fireEvent = true;

	var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);
	if(!validationChk_B()) return;
	var sKind		= "setogm090B_B";						
	var sMethodName = "service::psogmlib/saveOGM090B_B.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ogm090B_B=ds_ogm090B_B:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += " ADUSR=" + quote(idnum);
	sArgument += " LINCD=" + quote(LineCD);
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback1");
	http.Sync = false;
}

