/********************************************************************** 
 * 내용설명 : validation 체크 하단
 **********************************************************************/ 
function validationChk_B(){
	if (ds_ogm090B_B.lastrow>=0){
		if (length(ds_ogm090B_B.GetColumn(ds_ogm090B_B.lastrow,"MSHNO")) == 0){
			alert(setPopMessage("2540"));
			setMessage("2540");
			return false;
		}
		if (length(ds_ogm090B_B.GetColumn(ds_ogm090B_B.lastrow,"MMSHI")) == 0){
			ds_ogm090B_B.SetColumn(ds_ogm090B_B.lastrow,"MMSHI","0");
		}
	}
	return true;
}
/********************************************************************** 
 * 내용설명 : validation 체크 하단
 **********************************************************************/ 
function ds_ogm090B_B_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if( strColumnID == "MSHNO" ){
		if( length(trim(varNewValue)) == 0 ){
			alert(setPopMessage("2540"));
			setMessage("2540");
			return false;
		}
		var sKind		= "getogm090B_B_Chk";
		var sMethodName = "service::psogmlib/selectOGM090B_B_Chk.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_ogm090B_B_Chk=ds_ogm090B_B_Chk";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		//sArgument += " TABLENM_B=" + quote(tableNm_B); //TABLE명
		//sArgument += " FIELD1_B=" + quote(field1_B); //FIELD명1
		sArgument += " FVALUE=" + quote(varNewValue); //FIELD명1
		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_ogm090B_B_Chk.GetRowCount() != 0){
			alert(setPopMessage("2533"));
			setMessage("2533");
			return false;
		}
	}

	if( strColumnID == "MMSHI" ){
		if(!(trim(varNewValue) == '0' || trim(varNewValue) == '1' )){
			alert(setPopMessage("2545"));
			setMessage("2545");
			return false;
		}
	}

	if(strColumnID == "DELGB" ){
		if (varNewValue == "1"){
			var code = ds_ogm090B_B.GetColumn(nRow,"MSHNO");

			//var tableNm = substr(cmb_Code.Value,42,8);
			//var field1 = substr(cmb_Code.Value,30,5);

			var sKind		= "getogm090B_B_DelChk";
			var sMethodName = "service::psogmlib/selectOGM090B_B_DelChk.do";     		// 서버에서 호출 될 Method 명
			var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
			var sOutDataSet = "ds_ogm090B_B_DelChk=ds_ogm090B_B_DelChk";    				// 조회 후 돌려 받을 DataSet 명칭
			var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			//sArgument += " TABLENM=" + quote(tableNm); //TABLE명
			//sArgument += " FIELD1=" + quote(field1); //FIELD명1
			sArgument += " CODE=" + quote(code); //FIELD명1
			http.Sync = true;
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_ogm090B_B_DelChk.GetColumn(0,"MSHNO") != "0"){
				alert(setPopMessage("2537"));
				setMessage("2537");
				return false;
			}
		}
	}
	http.usewaitcursor = true;
}

function ds_ogm090B_B_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "MSHNO" ){
		ds_ogm090B_B.SetColumn(nRow,"MSHNO",toUpper(varNewValue));
	}
}

function chkbyte_db2(snewVal, slenLimit)
{
//	http.usewaitcursor = false;
	var chkbyte = 0;
	var msgVal = snewVal;
	var tmpVal = "0";
	var tmpch = "";
	var ch = "";
	for(var i = 0 ; i < msgVal.length; i++){
			temval = ch;
			ch =Asc(msgVal.CharAt(i));

		if(i == (msgVal.length)-1){
			if( length(urlEncode(ch)) > 4){
				if(length(urlEncode(temval)) > 4){
					chkbyte +=3;
				}
				else {
					chkbyte +=4;
				}
			}
			else{
				if(length(urlEncode(temval)) > 4){
					chkbyte +=2;
				}
				else {
					chkbyte++;
				}
			}

		}
		else{
			if( length(urlEncode(ch)) > 4){
				if(length(urlEncode(temval)) > 4){
					chkbyte +=2;
				}
				else {
					chkbyte +=3;
				}
			}
			else{
				if(length(urlEncode(temval)) > 4){
					chkbyte +=2;
				}
				else {
					chkbyte++;
				}
			}
		}
		if(chkbyte > slenLimit){
			//ds_ogm080A.fireEvent = false;
			alert(setPopMessage("2561"));
			var dcVal=substr(msgVal,0,i);
			//ds_ogm080A.fireEvent = true;
			return dcVal;
		}
	}
//	http.usewaitcursor = true;
}
