function ds_ogm090F_A_OnRowPosChanged(obj,nOldRow,nRow)
{
	//하단 라인 조회
	var sKind		= "getOGM090F";						
	var sMethodName = "service::psogmlib/selectOGM090F.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm090F=ds_ogm090F";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부	
		sArgument += " PRDCD=" + quote(Div0.st_PRDCD.Text); //제품코드
	http.Sync 	= true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
}

