/********************************************************************** 
 * 내용설명 : 하단조회
 **********************************************************************/ 
function Div0_btnAdd_OnClick(obj)
{
	if(checkInsertCount("ds_ogm090F")== false) return;
	ds_ogm090F.AddRow();
	ds_ogm090F.SetColumn(ds_ogm090F.currow,"COMPS", ComboBoxPlant.Value);
	ds_ogm090F.SetColumn(ds_ogm090F.currow,"PRDCD", Div0.st_PRDCD.Text);

}

/********************************************************************** 
 * 내용설명 : 제품코드 라인리스트 저장
 **********************************************************************/ 
function Div0_btnSave_OnClick(obj)
{
	if(ds_ogm090F.rowcount() < 1) {
		alert(setPopMessage("2547"));
		setMessage("2547");
		return;
	}

	ds_ogm090F.fireEvent = false;

	for(var z=ds_ogm090F.rowcount(); z>=0; z--){
		if (ds_ogm090F.GetColumn(z,"DELGB")=="1"){
			ds_ogm090F.DeleteRow(z);
		}
	}
	ds_ogm090F.fireEvent = true;

	var sKind		= "setogm090F";						
	var sMethodName = "service::psogmlib/saveOGM090F.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ogm090F=ds_ogm090F:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ogm090F_CHECK=ds_ogm090F_CHECK";		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " ADUSR=" + quote(G_ds_user.GetColumn(0,"USER_ID"));
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback2");
	http.Sync = false;
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback2(svcId, rtnCd, rtnMsg){
	//trace(ds_SLTZ101_CHECK.SaveXML());
	if (ds_ogm090F_CHECK.rowcount > 0){			
		for(var i=0; i<ds_ogm090F_CHECK.rowcount; i++){		
			alert("제품코드 : " +ds_ogm090F_CHECK.GetColumn(i,"PRDCD") + ", 라인코드 : " +ds_ogm090F_CHECK.GetColumn(i,"LINCD") + " = " + ds_ogm090F_CHECK.GetColumn(i,"RTMSG"));
		}
		ds_ogm090F_A_OnRowPosChanged();        //error항목이 존재시에는 재조회		
		return;		
	}	
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}else {
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}
function ds_ogm090F_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "LINCD"){//라인코드 확인
		if(varNewValue!=""){
			http.Sync = true;
			var sKind		= "getOBC030PF";		
			var sMethodName = "service::psogmlib/selectOBC030PF.do"; 
			var sInDataSet  = "";
			var sOutDataSet = "ds_OBC030PF=ds_OBC030PF";
			var sArgument   = "";
				sArgument  += " LIB2=" + quote(Lib2);
				sArgument  += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
				sArgument  += " LINCD=" + quote(varNewValue);
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			http.Sync = false;
			if (ds_OBC030PF.GetRowCount() < 1){
					alert(setPopMessage("4062")); //[확인] 사용자가 존재하지 않습니다.
					setMessage("4062");	
					return false;
			}
			p_LINNM=ds_OBC030PF.GetColumn(0,"LINNM");
		}
	}
	http.usewaitcursor = true;
}

function ds_ogm090F_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "LINCD"){
		if(varNewValue!=""){
			ds_ogm090F.SetColumn(nRow, "LINNM", p_LINNM); // 라인명
		}else{
			ds_ogm090F.SetColumn(nRow, "LINNM", ""); // 라인명
		}
	}
}
