var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var nowDate = today();
var saveQuery ="F";

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	setClass(ComboBoxClass,ds_Class); //분류지정(1:예비품, 2:비품, 3:식자재)
	getComCode(); //공장구분 선택
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	
	MainGrid.UserData = MainGrid.Height;
	fn_Search();
}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053"); //상세화면 메시지
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.RowHeight = G_ds_design.GetColumn(0,"RowHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

/********************************************************************** 
 * 내용설명 : 화면 조회 
 **********************************************************************/ 
function getogm105(){
	var group = ComboBoxClass.Value;
	var gubun = rd_gubun.Value;
	if (gubun == "A"){
		var sKind		= "getOgm105";						
		var sMethodName = "service::psogmlib/selectOGM105_A.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_ogm105=ds_ogm105";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";

		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@GROUP=" + quote(group); //그룹
		sArgument += "@DPTCD=" + quote(trim(ed_dptcd.Value));      //관리부서코드
		var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
		if (saveQuery == "F"){
			if (str == 'T'){  //정상조회
				alert(setPopMessage("1004"));
				setMessage("1004");	
			}else if (str == 'B'){   //data값이 없을때
				alert(setPopMessage("1003"));
				setMessage("1003");	
			}else if (str == 'E'){   //error발생시
				alert(setPopMessage("1002"));
				setMessage("1002");	
			}
		}
	}else if(gubun == "B"){
		var sKind		= "getOgm105";						
		var sMethodName = "service::psogmlib/selectOGM105_B.do";     		// 서버에서 호출 될 Method 명
		var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "ds_ogm105=ds_ogm105";    				// 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";

		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@GROUP=" + quote(group); //그룹
		sArgument += "@DPTCD=" + quote(trim(ed_dptcd.Value));      //관리부서코드
		var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
		if (saveQuery == "F"){
			if (str == 'T'){  //정상조회
				alert(setPopMessage("1004"));
				setMessage("1004");	
			}else if (str == 'B'){   //data값이 없을때
				alert(setPopMessage("1003"));
				setMessage("1003");	
			}else if (str == 'E'){   //error발생시
				alert(setPopMessage("1002"));
				setMessage("1002");	
			}
		}
	}
}

/********************************************************************** 
 * 내용설명 : LIST발행
 **********************************************************************/ 
function imgbtnPrint_OnClick(obj,nX,nY)
{

	if(ds_ogm105.rowcount() < 1) {
		alert(setPopMessage("2509"));
		setMessage("2509");	
		return;
	}
	var iCount=0;
	for(var i=0; i<ds_ogm105.rowcount(); i++){
		if (ds_ogm105.GetColumn(i, "SEL") == "1"){
			iCount = iCount+1;
		}
	}
	if (iCount == 0){ 
		alert(setPopMessage("2510"));
		setMessage("2510");	
		return;
	}

	if(confirm("선택하신 품목을 발주후보로 등록 하시겠습니까? ") == false) return;

	var iNo = 0;
	for(var i=0; i<ds_ogm105.rowcount(); i++){
		if (ds_ogm105.GetColumn(i, "SEL") == "1" && parseInt(ds_ogm105.GetColumn(i, "NQTY")) > 0){
			if(length(trim(ds_ogm105.GetColumn(i, "NNO"))) != 0){
				http.Sync 	= true;
				var sKind		= "updateOGM105_PR";
				var sMethodName = "service::psogmlib/updateOGM105_PR.do";     // 서버에서 호출 될 Method 명
				var sInDataSet  = "ds_ogm105=ds_ogm105:U";               //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " GROUP=" + quote(ComboBoxClass.Value);
				sArgument += " ORDNO=" + quote(ds_ogm105.GetColumn(i, "NNO"));
				sArgument += " PRTNO=" + quote(ds_OGM105.GetColumn(i,"PRTNO"));//prtno
				sArgument += " ORQTY=" + quote(ds_OGM105.GetColumn(i,"NQTY"));
				sArgument += " JANQY=" + quote(ds_OGM105.GetColumn(i,"NQTY"));
				sArgument += " ORSTS=" + quote("10");
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
			}else{
				var ordnumber = 0;
				if (iNo == 0){
					maxNumberSelect();//발주번호구하기
					var max_no = ds_OGM105_maxNumber.GetColumn(0,"MAXORDNO");
					iNo = parseInt(max_no);
				}else{
					iNo = iNo+1;
				}
				if (iNo > 999999){
					ordnumber = "0"+iNo;
				}else if (iNo > 99999){
					ordnumber = "00"+iNo;
				}else if (iNo > 9999){
					ordnumber = "000"+iNo;
				}else{
					ordnumber = "0000"+iNo;
				}
				//insert
				var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);
				var n_dateWork = ds_OGM105.GetColumn(i,"NDATE");
				var n_date = n_dateWork;
				//var n_date = substr(n_dateWork,0,4)+substr(n_dateWork,5,2)+substr(n_dateWork,8,2);
				http.Sync 	= true;
				var sKind		= "insertOGM105_PR";						
				var sMethodName = "service::psogmlib/insertOGM105_PR.do";     // 서버에서 호출 될 Method 명
				var sInDataSet  = "ds_ogm105=ds_ogm105:U";               //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " GROUP=" + quote(ComboBoxClass.Value);  
				sArgument += " ORDNO=" + quote(ordnumber); 
				sArgument += " VNDOR=" + quote(ds_OGM105.GetColumn(i,"VNDOR"));//vndor
				sArgument += " PRTNO=" + quote(ds_OGM105.GetColumn(i,"PRTNO"));//prtno
				sArgument += " PUNIT=" + quote(ds_OGM105.GetColumn(i,"PUNIT"));//punit
				sArgument += " ORDAT=" + quote("0");
				sArgument += " DUEDT=" + quote(n_date);
				sArgument += " ORQTY=" + quote(ds_OGM105.GetColumn(i,"NQTY"));
				sArgument += " JANQY=" + quote(ds_OGM105.GetColumn(i,"NQTY"));
				sArgument += " OCOST=" + quote(ds_OGM105.GetColumn(i,"COST"));
				sArgument += " ORSTS=" + quote("10");
				sArgument += " INSTS=" + quote("N");
				sArgument += " PORNO=" + quote(" ");
				sArgument += " ORDID=" + quote("0");
				sArgument += " ORUSE=" + quote(" ");
				//addat,adtim
				sArgument += " ADUSR=" + quote(idnum);  //등록자
				//chdat,chtim
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
			}
		}
	}
	saveQuery="T";
	getOgm105();  //처리후 재조회

	//발행~
	reportQuery();
	dialog("psogmlib::ogm105rep.xml","");
	alert("인쇄가 완료되면 인쇄승인을 클릭하여 발주승인처리 해주세요");

}

