function imgbtnOK_OnClick(obj,nX,nY)
{
	if(confirm("승인처리 하시겠습니까?              ") == false) return;
	var sKind		= "updateOGM105_OK";						
	var sMethodName = "service::psogmlib/updateOGM105_OK.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_ogm105=ds_ogm105:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
	sArgument += " GROUP=" + quote(ComboBoxClass.Value);       //코드분류 
	sArgument += " ORSTS=" + quote("10");                   //발주상태 10=발주대기
	//target value
	sArgument += " PT1YN=" + quote("Y");
	sArgument += " PT2YN=" + quote("PT2YN");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");	
}

/********************************************************************** 
 * 내용설명 : 발주보류
 **********************************************************************/ 
function imgbtnHold_OnClick(obj,nX,nY)
{
	if(ds_ogm105.rowcount() < 1) {
		alert(setPopMessage("2509"));
		setMessage("2509");	
		return;
	}
	var iCount=0;
	for(var i=0; i<ds_ogm105.rowcount(); i++){
		if (ds_ogm105.GetColumn(i, "SEL") == "1"){
			iCount = iCount+1;
		}
	}
	if (iCount == 0){ 
		alert(setPopMessage("2510"));
		setMessage("2510");	
		return;
	}

	if(confirm("선택하신 품목을 발주보류 하시겠습니까? ") == false) return;

	for(var i=0; i<ds_ogm105.rowcount(); i++){
		if (ds_ogm105.GetColumn(i, "SEL") == "1" ){
			if(length(trim(ds_ogm105.GetColumn(i, "NNO"))) == 0){
				maxNumberSelect();//발주번호구하기
				var max_no = ds_OGM105_maxNumber.GetColumn(0,"MAXORDNO");
				var ordnumber = 0;
				if (length(max_no) > 4){
					ordnumber = "000"+max_no;
				}else{
					ordnumber = "0000"+max_no;
				}
				//insert
				var idnum = substr(G_ds_user.GetColumn(0,"USER_ID"),1,7);
				var n_dateWork = ds_OGM105.GetColumn(i,"NDATE");
				var n_date = n_dateWork;
				//var n_date = substr(n_dateWork,0,4)+substr(n_dateWork,5,2)+substr(n_dateWork,8,2);
				http.Sync 	= false;
				var sKind		= "insertOGM105_HOLD";						
				var sMethodName = "service::psogmlib/insertOGM105_PR.do";     // 서버에서 호출 될 Method 명
				var sInDataSet  = "ds_ogm105=ds_ogm105:U";               //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " GROUP=" + quote(ComboBoxClass.Value);  
				sArgument += " ORDNO=" + quote(ordnumber); 
				sArgument += " VNDOR=" + quote(ds_OGM105.GetColumn(i,"VNDOR"));//vndor
				sArgument += " PRTNO=" + quote(ds_OGM105.GetColumn(i,"PRTNO"));//prtno
				sArgument += " PUNIT=" + quote(ds_OGM105.GetColumn(i,"PUNIT"));//punit
				sArgument += " ORDAT=" + quote("0");
				sArgument += " DUEDT=" + quote(n_date);
				sArgument += " ORQTY=" + quote(ds_OGM105.GetColumn(i,"NQTY"));
				sArgument += " JANQY=" + quote(ds_OGM105.GetColumn(i,"NQTY"));
				sArgument += " OCOST=" + quote(ds_OGM105.GetColumn(i,"COST"));
				sArgument += " ORSTS=" + quote("50");
				sArgument += " INSTS=" + quote("N");
				sArgument += " PORNO=" + quote(" ");
				sArgument += " ORDID=" + quote("0");
				sArgument += " ORUSE=" + quote(" ");
				//addat,adtim
				sArgument += " ADUSR=" + quote(idnum);  //등록자
				//chdat,chtim
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
			}
		}
