	setMessage("1001");
}

/********************************************************************** 
 * 내용설명 : 삭제
 **********************************************************************/ 
function imgbtnDelete_OnClick(obj,nX,nY)
{
	if(ds_ogm105.rowcount() < 1) {
		alert(setPopMessage("2509"));
		setMessage("2509");	
		return;
	}
	var iCount=0;
	for(var i=0; i<ds_ogm105.rowcount(); i++){
		if (ds_ogm105.GetColumn(i, "SEL") == "1"){
			iCount = iCount+1;
		}
	}
	if (iCount == 0){ 
		alert(setPopMessage("2510"));
		setMessage("2510");	
		return;
	}

	if(confirm("선택하신 품목을 삭제 하시겠습니까? ") == false) return;

	for(var i=0; i<ds_ogm105.rowcount(); i++){
		if (ds_ogm105.GetColumn(i, "SEL") == "1" ){
			if(length(trim(ds_ogm105.GetColumn(i, "NNO"))) != 0){
				var sKind		= "deleteOGM105";						
				var sMethodName = "service::psogmlib/deleteOGM105.do";     // 서버에서 호출 될 Method 명
				var sInDataSet  = "ds_ogm105=ds_ogm105:U";               //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += " LIB2=" + quote(Lib2);
				sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
				sArgument += " GROUP=" + quote(ComboBoxClass.Value);
				sArgument += " ORDNO=" + quote(ds_ogm105.GetColumn(i,"NNO"));
				sArgument += " PRTNO=" + quote(ds_ogm105.GetColumn(i,"PRTNO"));//prtno
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
			}	
		}
	}
	saveQuery="T";
	getOgm105();  //처리후 재조회
	alert(setPopMessage("1001"));
	setMessage("1001");
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="updateOGM105_OK"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

/********************************************************************** 
 * 내용설명 : 발주수량변경시 Checkbox에 Check상태로 변경
 **********************************************************************/ 
function ds_ogm105_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "NQTY" ){
		ds_ogm105.SetColumn(nRow,"SEL","1");
	}
	if( strColumnID == "NDATE" ){
		ds_ogm105.SetColumn(nRow,"SEL","1");
	}
}

/********************************************************************** 
 * 내용설명 : 부서명 조회
 **********************************************************************/ 
function imgDptcd_OnClick(obj,nX,nY)
{
	var sDptcd=trim(ed_dptcd.Text);
	if (length(trim(ed_dptcd.Text)) == 0) sDptcd="zzzzzzzzzz";
	var selectnumber = "6";    // 1.코드 2.품목 3.업체 4.Maker 5.사용자 , 6.부서  7.설비 8.사원
	var rtn = dialog("PSOGMLIB::ogmSelect.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "SEL=" + selectnumber + " " + "CODE=" + sDptcd,584,528);
	if (length(rtn) != 0){
		ed_dptcd.Text = rtn[0];
		st_dptcdnm.Text = rtn[1];
	}
}

/********************************************************************** 
 * 내용설명 : 부서명 조회
 **********************************************************************/ 
function ed_dptcd_OnCharChanged(obj,strPreText,strPostText)
{
	st_dptcdnm.Text = "";
	if(length(trim(ed_dptcd.Value)) == 4){
		var sKind		= "getogm105_dchk";						
		var sMethodName = "service::pscomlib/selectMST650.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_mst650PF=ds_mst650PF";
		var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value);
		sArgument += " DPCODE=" + quote(trim(ed_dptcd.Value));       //부서코드
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
}


/********************************************************************** 
