 **********************************************************************/ 
function setExcel()
{
	var fName   = "발주후보관리";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|발주후보";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


