	ds_ogm105.ClearData();
}

function rd_gubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//ds_ogm105.DeleteAll();
	ds_ogm105.ClearData();
	getOgm105();
	if(strCode == "A"){
		imgbtnDelete.Visible = false;
	}else if(strCode == "B"){
		imgbtnDelete.Visible = true;
	}
}
