function ds_ogm105_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "NDATE" ){
		if( length(varNewValue) == 0 ){
			alert(setPopMessage("2008"));  //[알림] 날짜 형식이 아닙니다.
			setMessage("2008");	
			return false;
		}
	}
	if(strColumnID == "NQTY"){
		if (ToNumber(varNewValue) < 1) {
			alert(setPopMessage("2517"));
			setMessage("2517");	
			return false;
		}
	}
}
